int DECODE_3Gray2int(int n,int POS[]){

	int SDEC[16];
	int k,l;
	int jdg,keta;
	int ret;

	for(k=0;k<16;k++)SDEC[k]=0;
	for(k=n-1;k>=0;k--){
		jdg=0;
		keta=1;
		for(l=1;k+l<=n;l++){
			jdg=jdg+keta*SDEC[k+l];
			keta*=3;
		}
		SDEC[k]=jdg%2?(2-POS[k]):POS[k];
	}
//dbg for(k=0;k<n;k++)printf("%d ",SDEC[k]);puts("");

	keta=1;ret=0;
	for(k=0;k<16;k++){
		ret+=SDEC[k]*keta;
		keta*=3;
	}

	return ret;
}

