import java.io.IOException;
import java.util.Scanner;

public class Main {

	public static void main(String[] args) throws IOException {

		new Main().run();
	}

	private void run() throws IOException {
		Scanner scanner = new Scanner(System.in);
		while (true) {
			int n = scanner.nextInt();
			int m = scanner.nextInt();
			if ((n | m) == 0)
				break;
			int[] pow3 = new int[n + 1];
			pow3[0] = 1;
			for (int i = 1; i <= n; i++)
				pow3[i] = pow3[i - 1] * 3;
			int[] c = new int[n];
			for (int i = 0; i < 3; i++) {
				int a = scanner.nextInt();
				for (int j = a; j > 0; j--) {
					int k = scanner.nextInt();
					c[n - k] = i;
				}
			}
			int p = 0;
			int x = 0;
			for (int i = n - 1; i >= 0; i--) {
				int d = Math.abs(p - c[i]);
				x += pow3[i] * d;
				if (d == 1)
					p = 2 - p;
			}
			int y = pow3[n] - 1 - x;
			if (x > y)
				x = y;
			System.out.println(x > m ? -1 : x);
		}

	}
}

import java.io.*;
import static java.lang.Integer.*;
import java.util.Stack;

class Main{
    static int n,m,ans=0;

    static Stack<Integer> stackA_ini;
    static Stack<Integer> stackB_ini;
    static Stack<Integer> stackC_ini;

    static Stack<Integer> stackA;
    static Stack<Integer> stackB;
    static Stack<Integer> stackC;

    public static void main(String[] a)throws IOException{
        while(true){
            inputData();
            ans = (int)min(calcFromLeft(),calcFromRight());
            ans = (ans <= m) ? ans : -1;               
            System.out.println(ans);
        }

    }
    static int calcFromLeft(){
        int step = 0;
        initializeStack();

        //
        while(true){
            // 
            if(isFinished()) break;

            // 
            if(peekStack(stackA)>peekStack(stackB))
                stackB.push(stackA.pop());
            else
                stackA.push(stackB.pop());

            step++;

            // 
            if(isFinished()) break;

            // 
            if(peekStack(stackB)>peekStack(stackC))
                stackC.push(stackB.pop());
            else
                stackB.push(stackC.pop());

            step++;
        }
        return step;
    }
    static int calcFromRight(){

        int step = 0;
        initializeStack();

        //
        while(true){     
            // 
            if(isFinished()) break;

            // 
            if(peekStack(stackB)>peekStack(stackC))
                stackC.push(stackB.pop());
            else
                stackB.push(stackC.pop());

            step++;

            // 
            if(isFinished()) break;          

            // 
            if(peekStack(stackA)>peekStack(stackB))
                stackB.push(stackA.pop());
            else
                stackA.push(stackB.pop());

            step++;
        }
        return step;
    }

    static void initializeStack(){
        stackA = (Stack<Integer>) stackA_ini.clone();
        stackB = (Stack<Integer>) stackB_ini.clone();
        stackC = (Stack<Integer>) stackC_ini.clone();
    }

    static boolean isFinished(){
        if(stackB.empty() && (stackA.empty() || stackC.empty()))
            return true;
        else
            return false;
    }
    static int peekStack(Stack<Integer> stack){
        return stack.empty() ? 0:stack.peek();      
    }

    static void inputData() throws IOException{
        BufferedReader in = new BufferedReader(new InputStreamReader(System.in));
        String[] nm = in.readLine().split(" ");
        String[] cupList;
        int cupNum = 0;

        stackA.clear();
        stackB.clear();
        stackC.clear();

        n = Integer.parseInt(nm[0]);
        m = Integer.parseInt(nm[1]);

        if(n+m == 0){
            System.exit(0);
        }

        cupList = in.readLine().split(" ");
        for(int j=0; Integer.parseInt(cupList[0])>j; j++){
            stackA_ini.push(Integer.parseInt(cupList[j+1]));
        }

        cupList = in.readLine().split(" ");
        for(int j=0; Integer.parseInt(cupList[0])>j; j++){
            stackB_ini.push(Integer.parseInt(cupList[j+1]));
        }

        cupList = in.readLine().split(" ");
        for(int j=0; Integer.parseInt(cupList[0])>j; j++){
            stackC_ini.push(Integer.parseInt(cupList[j+1]));
        }
    }
}

import java.util.*;

public class Main {

	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);

		while(true){
			int n = sc.nextInt();
			int m = sc.nextInt();
			if(n==0 && m==0) break;

			int[] a = new int[n+1];
			int[] b = new int[n+1];
			int[] c = new int[n+1];
			int[] d = new int[n+1];
			int[] e = new int[n+1];
			int[] f = new int[n+1];

			int an = 0, bn = 0, cn = 0, dn = 0, en = 0, fn = 0;

			dn = sc.nextInt();
			for(int i=1;i<=dn;i++) d[i] = sc.nextInt();
			en = sc.nextInt();
			for(int i=1;i<=en;i++) e[i] = sc.nextInt();
			fn = sc.nextInt();
			for(int i=1;i<=fn;i++) f[i] = sc.nextInt();

			int ans = Integer.MAX_VALUE;
			int[] ini = new int[2];
			int com;

			if(d[dn]>e[en] && e[en]>f[fn]){
				ini[0] = 0;
				ini[1] = 2;
			}else if(d[dn]>e[en] && e[en]<f[fn]){
				ini[0] = 0;
				ini[1] = 3;
			}else if(d[dn]<e[en] && e[en]<f[fn]){
				ini[0] = 1;
				ini[1] = 3;
			}

			for(int k=0;k<2;k++){
				com = ini[k];
				for(int i=0;i<=n;i++){
					a[i] = d[i];
					b[i] = e[i];
					c[i] = f[i];
				}
				an = dn;
				bn = en;
				cn = fn;
				for(int i=0;i<=m;i++){
					if(bn==0 && (an==0 || cn==0)){
						ans = Math.min(ans, i);
						break;
					}

					if(com==0){
						bn++;
						b[bn] = a[an];
						an--;
						if(b[bn]>c[cn]) com = 2;
						else com = 3;
					}else if(com==1){
						an++;
						a[an] = b[bn];
						bn--;
						if(b[bn]>c[cn]) com = 2;
						else com = 3;
					}else if(com==2){
						cn++;
						c[cn] = b[bn];
						bn--;
						if(a[an]>b[bn]) com = 0;
						else com = 1;
					}else if(com==3){
						bn++;
						b[bn] = c[cn];
						cn--;
						if(a[an]>b[bn]) com = 0;
						else com = 1;
					}
				}
			}

			if(ans==Integer.MAX_VALUE) System.out.println(-1);
			else System.out.println(ans);
		}	
	}	
}

