class Cup {
	private int _num;
	private int _max;
	private Stack<Integer> _tray_a;
	private Stack<Integer> _tray_b;
	private Stack<Integer> _tray_c;
	private OPERATION _prev_op;

	public Cup(int num, int max) {
		_num = num;
		_max = max;
		_tray_a = new Stack<Integer>();
		_tray_b = new Stack<Integer>();
		_tray_c = new Stack<Integer>();
		_prev_op = OPERATION.FIRST_STEP;
	}

	public void setPrevOp(OPERATION op) { _prev_op = op; }

	public boolean isEnd() {
		return _tray_a.size() == _num || _tray_c.size() == _num;
	}

	public void setTrayA(String str) {
		_tray_a = createTray(str);
	}
	public void setTrayB(String str) {
		_tray_b = createTray(str);
	}
	public void setTrayC(String str) {
		_tray_c = createTray(str);
	}

	private Stack<Integer> createTray(String str) {
		Stack<Integer> stack = new Stack<Integer>();
		StringTokenizer st = new StringTokenizer(str);
		int num = Integer.valueOf(st.nextToken());
		for(int i = 0; i < num; i++) {
			Integer e = new Integer(st.nextToken());
			stack.push( e );
		}
		return stack;
	}

	public void operate() {
		int top_a = ( _tray_a.empty() ) ? 0 : _tray_a.get( _tray_a.size()-1 );
		int top_b = ( _tray_b.empty() ) ? 0 : _tray_b.get( _tray_b.size()-1 );
		int top_c = ( _tray_c.empty() ) ? 0 : _tray_c.get( _tray_c.size()-1 );
		if( top_a > top_b && _prev_op != OPERATION.B_TO_A ) {
			_tray_b.push( _tray_a.pop() );
			_prev_op = OPERATION.A_TO_B;
		} else if( top_b > top_a && _prev_op != OPERATION.A_TO_B ) {
			_tray_a.push( _tray_b.pop() );
			_prev_op = OPERATION.B_TO_A;
		} else if( top_b > top_c && _prev_op != OPERATION.C_TO_B ) {
			_tray_c.push( _tray_b.pop() );
			_prev_op = OPERATION.B_TO_C;
		} else if( top_c > top_b && _prev_op != OPERATION.B_TO_C ) {
			_tray_b.push( _tray_c.pop() );
			_prev_op = OPERATION.C_TO_B;
		}
	}

	public int solve() {
		boolean isImpossible = true;
		int count;
//		printCup();
		for(count = 1; count <= _max; count++) {
			operate();
//			printCup();
			if(isEnd()) {
				isImpossible = false;
				break;
			}
		}
		if(isImpossible) {
			count = -1;
		}
		return count;
	}

	public int getOperationAvailable() {
		int ret = 0;
		int top_a = ( _tray_a.empty() ) ? 0 : _tray_a.get( _tray_a.size()-1 );
		int top_b = ( _tray_b.empty() ) ? 0 : _tray_b.get( _tray_b.size()-1 );
		int top_c = ( _tray_c.empty() ) ? 0 : _tray_c.get( _tray_c.size()-1 );
		if( top_a > top_b  ) {
			ret |= 1;
		} 
		if( top_b > top_a  ) {
			ret |= 1 << 1;
		} 
		if( top_b > top_c  ) {
			ret |= 1 << 2;
		} 
		if( top_c > top_b ) {
			ret |= 1 << 3;
		}
		return ret;
	}

	public void printCup() {
		System.out.print("A: ");
		for(int i = 0; i < _tray_a.size(); i++ ) {
			System.out.print(_tray_a.get(i) + " ");
		}
		System.out.println();
		System.out.print("B: ");
		for(int i = 0; i < _tray_b.size(); i++ ) {
			System.out.print(_tray_b.get(i) + " ");
		}
		System.out.println();
		System.out.print("C: ");
		for(int i = 0; i < _tray_c.size(); i++ ) {
			System.out.print(_tray_c.get(i) + " ");
		}
		System.out.println();
		System.out.println();
	}
}

import java.io.*;
import java.util.ArrayList;

class Main{
    static int n,m,ans=0;

    static ArrayList<Integer> stackA_ini ;    
    static ArrayList<Integer> stackB_ini ;    
    static ArrayList<Integer> stackC_ini ;    

    static ArrayList<Integer> stackA;
    static ArrayList<Integer> stackB;
    static ArrayList<Integer> stackC;    

    static BufferedReader in = new BufferedReader(new InputStreamReader(System.in));

    public static void main(String[] a)throws IOException{
        while(true){
            inputData();
            ans = Math.min(calcFromLeft(),calcFromRight());
            ans = (ans <= m) ? ans : -1;               
            System.out.println(ans);
        }
    }

    static int calcFromLeft(){
        int step = 0;

        //
        while(true){
            // 
            if(isFinished()) break;

            // 
            if(peekStack(stackA)>peekStack(stackB)){
                stackB.add(stackA.get(stackA.size()-1));
                stackA.remove(stackA.size()-1);
            }else{
                stackA.add(stackB.get(stackB.size()-1));
                stackB.remove(stackB.size()-1);
            }

            step++;

            // 
            if(isFinished()) break;

            // 
            if(peekStack(stackB)>peekStack(stackC)){
                stackC.add(stackB.get(stackB.size()-1));
                stackB.remove(stackB.size()-1);
            }else{
                stackB.add(stackC.get(stackC.size()-1));
                stackC.remove(stackC.size()-1);
            }
            step++;
        }
        return step;
    }

    static int calcFromRight(){

        int step = 0;
        stackA = stackA_ini;
        stackB = stackB_ini;
        stackC = stackC_ini;

        //
        while(true){     
            // 
            if(isFinished()) break;

            // 
            if(peekStack(stackB)>peekStack(stackC)){
                stackC.add(stackB.get(stackB.size()-1));
                stackB.remove(stackB.size()-1);
            }else{
                stackB.add(stackC.get(stackC.size()-1));
                stackC.remove(stackC.size()-1);
            }
            step++;

            // 
            if(isFinished()) break;          

            // 
            if(peekStack(stackA)>peekStack(stackB)){
                stackB.add(stackA.get(stackA.size()-1));
                stackA.remove(stackA.size()-1);
            }else{
                stackA.add(stackB.get(stackB.size()-1));
                stackB.remove(stackB.size()-1);
            }

            step++;
        }
        return step;
    }

    static boolean isFinished(){
        if(stackB.isEmpty() && (stackA.isEmpty() || stackC.isEmpty()))
            return true;
        else
            return false;
    }

    static int peekStack(ArrayList<Integer> stack){
        return stack.isEmpty() ? 0:stack.get(stack.size()-1);      
    }

    static void inputData() throws IOException{

        String[] nm = in.readLine().split(" ");
        String[] cupList;
        int cupNum = 0;

        stackA = new ArrayList<Integer>();
        stackB = new ArrayList<Integer>();
        stackC = new ArrayList<Integer>();
        stackA_ini = new ArrayList<Integer>();
        stackB_ini = new ArrayList<Integer>();
        stackC_ini = new ArrayList<Integer>();        

        n = Integer.parseInt(nm[0]);
        m = Integer.parseInt(nm[1]);

        if(n+m == 0){
            System.exit(0);
        }

        cupList = in.readLine().split(" ");
        for(int j=0; Integer.parseInt(cupList[0])>j; j++){
            stackA_ini.add(Integer.parseInt(cupList[j+1]));
            stackA.add(Integer.parseInt(cupList[j+1]));
        }

        cupList = in.readLine().split(" ");
        for(int j=0; Integer.parseInt(cupList[0])>j; j++){
            stackB_ini.add(Integer.parseInt(cupList[j+1]));
            stackB.add(Integer.parseInt(cupList[j+1]));
        }

        cupList = in.readLine().split(" ");
        for(int j=0; Integer.parseInt(cupList[0])>j; j++){
            stackC_ini.add(Integer.parseInt(cupList[j+1]));
            stackC.add(Integer.parseInt(cupList[j+1]));
        }
    }
}

