		}
		/*
		//???????????top????????
		for (int i = 0; i < 3; i++){
			System.out.print(trays[i].top());
		}
		System.out.println();
		*/

		Tray[] trayCopy = new Tray[3];
		//A to B
		if(prevmode != 3 && trays[0].top() > trays[1].top()) {
			copyTray(trayCopy, trays);
			moveCup(trayCopy, m - 1, 0, prevmode);
		}
		if(prevmode != 2 && trays[1].top() > trays[2].top()) {
			copyTray(trayCopy, trays);
			moveCup(trayCopy, m - 1, 1, prevmode);
		}
		if(prevmode != 1 && trays[2].top() > trays[1].top()) {
			copyTray(trayCopy, trays);
			moveCup(trayCopy.clone(), m - 1, 2, prevmode);
		}
		if(prevmode != 0 && trays[1].top() > trays[0].top()) {
			copyTray(trayCopy, trays);
			moveCup(trayCopy.clone(), m - 1, 3, prevmode);
		}
	}

	private void copyTray(Tray[] newTray, Tray[] origin){
		for (int i = 0 ; i < origin.length; i++){
			newTray[i] = new Tray(new ArrayList<Integer>(origin[i].getCups()));
		}
	}

	private boolean emptyCheck (Tray[] trays){
		if(trays[0].isEmpty() && trays[1].isEmpty()) {
			return true;
		}
		else if (trays[1].isEmpty() && trays[2].isEmpty()) {
			return true;
		}
		else {
			return false;
		}
	}
