	static class TaskX {

		long[] pow3 = new long[20];
		public void solve(int testNumber, MyInput in, PrintWriter out) {

			pow3[0] = 1;
			for (int i = 1; i < 20; i++) {
				pow3[i] = pow3[i-1] * 3;
			}

			int n = in.nextInt(), m = in.nextInt();
			while (!(m == 0 && n == 0)) {

				int[] c = new int[20];
				for (int i = 0; i < 3; i++) {
