import java.io.*;
import static java.lang.Integer.*;
import java.util.Stack;

class Main{
    static int n,m,ans=0;

    static Stack<Integer> stackA_ini;
    static Stack<Integer> stackB_ini;
    static Stack<Integer> stackC_ini;

    static Stack<Integer> stackA;
    static Stack<Integer> stackB;
    static Stack<Integer> stackC;

    public static void main(String[] a)throws IOException{
        while(true){
            inputData();
            ans = min(calcFromLeft(),calcFromRight());
            ans = (ans <= m) ? ans : -1;               
            System.out.println(ans);
        }

    }
    static int calcFromLeft(){
        int step = 0;
        initializeStack();

        //
        while(true){
            // 
            if(isFinished()) break;

            // 
            if(peekStack(stackA)>peekStack(stackB))
                stackB.push(stackA.pop());
            else
                stackA.push(stackB.pop());

            step++;

            // 
            if(isFinished()) break;

            // 
            if(peekStack(stackB)>peekStack(stackC))
                stackC.push(stackB.pop());
            else
                stackB.push(stackC.pop());

            step++;
        }
        return step;
    }
    static int calcFromRight(){

        int step = 0;
        initializeStack();

        //
        while(true){     
            // 
            if(isFinished()) break;

            // 
            if(peekStack(stackB)>peekStack(stackC))
                stackC.push(stackB.pop());
            else
                stackB.push(stackC.pop());

            step++;

            // 
            if(isFinished()) break;          

            // 
            if(peekStack(stackA)>peekStack(stackB))
                stackB.push(stackA.pop());
            else
                stackA.push(stackB.pop());

            step++;
        }
        return step;
    }

    static void initializeStack(){
        stackA = (Stack<Integer>) stackA_ini.clone();
        stackB = (Stack<Integer>) stackB_ini.clone();
        stackC = (Stack<Integer>) stackC_ini.clone();
    }

    static boolean isFinished(){
        if(stackB.empty() && (stackA.empty() || stackC.empty()))
            return true;
        else
            return false;
    }
    static int peekStack(Stack<Integer> stack){
        return stack.empty() ? 0:stack.peek();      
    }

    static void inputData() throws IOException{
        BufferedReader in = new BufferedReader(new InputStreamReader(System.in));
        String[] nm = in.readLine().split(" ");
        String[] cupList;
        int cupNum = 0;

        stackA.clear();
        stackB.clear();
        stackC.clear();

        n = Integer.parseInt(nm[0]);
        m = Integer.parseInt(nm[1]);

        if(n+m == 0){
            System.exit(0);
        }

        cupList = in.readLine().split(" ");
        for(int j=0; Integer.parseInt(cupList[0])>j; j++){
            stackA_ini.push(Integer.parseInt(cupList[j+1]));
        }

        cupList = in.readLine().split(" ");
        for(int j=0; Integer.parseInt(cupList[0])>j; j++){
            stackB_ini.push(Integer.parseInt(cupList[j+1]));
        }

        cupList = in.readLine().split(" ");
        for(int j=0; Integer.parseInt(cupList[0])>j; j++){
            stackC_ini.push(Integer.parseInt(cupList[j+1]));
        }
    }
}

import java.util.Scanner;

//Cup
public class Main{

	int[] last;
	int N, M;

	int f(int[] c, int from, int to){
		int res = 1;
		boolean con = true;
		while(!(c[0]==(1<<N)-1||c[2]==(1<<N)-1)&&res<=M){
			con = true;
			for(int f=0;f<3&&con;f++)for(int t=0;t<3&&con;t++){
				if(Math.abs(f-t)>1)continue;
				if(from==t&&to==f)continue;
				if(last[c[f]]<=last[c[t]])continue;
				c[t]+=1<<last[c[f]]; c[f]-=1<<last[c[f]];
				from = f; to = t;
				con = false;
			}
			res++;
		}
		return res;
	}

	void run(){
		last = new int[1<<15];
		for(int S=0;S<1<<15;S++){
			int k = 14;
			while(k>=0&&((S>>k)&1)==0)k--;
			last[S] = k;
		}
		Scanner sc = new Scanner(System.in);
		for(;;){
			N = sc.nextInt(); M = sc.nextInt();
			if((N|M)==0)break;
			int[] cup = new int[3];
			for(int i=0;i<3;i++){
				int k = sc.nextInt();
				while(k--!=0)cup[i]+=1<<(sc.nextInt()-1);
			}
			if(cup[0]==(1<<N)-1||cup[2]==(1<<N)-1){
				System.out.println(0); continue;
			}
			int res = M+1;
			for(int f=0;f<3;f++)for(int t=0;t<3;t++){
				if(Math.abs(f-t)>1)continue;
				if(last[cup[f]]<=last[cup[t]])continue;
				int[] c = new int[3];
				c[0] = cup[0]; c[1] = cup[1]; c[2] = cup[2];
				c[t]+=1<<last[c[f]]; c[f]-=1<<last[c[f]];
				res = Math.min(res, f(c, f, t));
			}
			System.out.println(M<res?"-1":res);
		}
	}

	public static void main(String[] args) {
		new Main().run();
	}
}

import java.io.*;
import static java.lang.Integer.min;
import java.util.Stack;

class Main{
    static int n,m,ans=0;

    static Stack<Integer> stackA_ini = new Stack<>();
    static Stack<Integer> stackB_ini = new Stack<>();
    static Stack<Integer> stackC_ini = new Stack<>();

    static Stack<Integer> stackA = new Stack<>();
    static Stack<Integer> stackB = new Stack<>();
    static Stack<Integer> stackC = new Stack<>();

    public static void main(String[] a)throws IOException{
        while(true){
            inputData();
            ans = min(calcFromLeft(),calcFromRight());
            ans = (ans <= m) ? ans : -1;               
            System.out.println(ans);
        }

    }
    static int calcFromLeft(){
        int step = 0;
        initializeStack();

        //
        while(true){
            // 
            if(isFinished()) break;

            // 
            if(peekStack(stackA)>peekStack(stackB))
                stackB.push(stackA.pop());
            else
                stackA.push(stackB.pop());

            step++;

            // 
            if(isFinished()) break;

            // 
            if(peekStack(stackB)>peekStack(stackC))
                stackC.push(stackB.pop());
            else
                stackB.push(stackC.pop());

            step++;
        }
        return step;
    }
    static int calcFromRight(){

        int step = 0;
        initializeStack();

        //
        while(true){     
            // 
            if(isFinished()) break;

            // 
            if(peekStack(stackB)>peekStack(stackC))
                stackC.push(stackB.pop());
            else
                stackB.push(stackC.pop());

            step++;

            // 
            if(isFinished()) break;          

            // 
            if(peekStack(stackA)>peekStack(stackB))
                stackB.push(stackA.pop());
            else
                stackA.push(stackB.pop());

            step++;
        }
        return step;
    }

    static void initializeStack(){
        stackA = (Stack<Integer>) stackA_ini.clone();
        stackB = (Stack<Integer>) stackB_ini.clone();
        stackC = (Stack<Integer>) stackC_ini.clone();
    }

    static boolean isFinished(){
        if(stackB.empty() && (stackA.empty() || stackC.empty()))
            return true;
        else
            return false;
    }
    static int peekStack(Stack<Integer> stack){
        return stack.empty() ? 0:stack.peek();      
    }

    static void inputData() throws IOException{
        BufferedReader in = new BufferedReader(new InputStreamReader(System.in));
        String[] nm = in.readLine().split(" ");
        String[] cupList;
        int cupNum = 0;

        stackA.clear();
        stackB.clear();
        stackC.clear();

        n = Integer.parseInt(nm[0]);
        m = Integer.parseInt(nm[1]);

        if(n+m == 0){
            System.exit(0);
        }

        cupList = in.readLine().split(" ");
        for(int j=0; Integer.parseInt(cupList[0])>j; j++){
            stackA_ini.push(Integer.parseInt(cupList[j+1]));
        }

        cupList = in.readLine().split(" ");
        for(int j=0; Integer.parseInt(cupList[0])>j; j++){
            stackB_ini.push(Integer.parseInt(cupList[j+1]));
        }

        cupList = in.readLine().split(" ");
        for(int j=0; Integer.parseInt(cupList[0])>j; j++){
            stackC_ini.push(Integer.parseInt(cupList[j+1]));
        }
    }
}

