	void run() {
		Scanner sc = new Scanner(System.in);
		for(;;) {
			n = sc.nextInt();
			m = sc.nextInt();
			if( (n|m) == 0 ) break;
			LinkedList<Integer>[] cups = new LinkedList[3+2];
			cups[0] = new LinkedList<Integer>();
			cups[4] = new LinkedList<Integer>();
			cups[0].add(30);
			cups[4].add(30);

			for(int i=1;i<=3;i++) {
				cups[i] = new LinkedList<Integer>();
				cups[i].add(0);
				int k = sc.nextInt();
				for(int j=0;j<k;j++) cups[i].add(sc.nextInt());
			}
			if( cups[1].size() == n+1 || cups[3].size() == n+1 ) System.out.println(0);
			int c = cups[2].getLast();
			int left = cups[1].getLast();
			int right = cups[3].getLast();
			int cnt = 0;
			if( c > max(left, right) ) {
				LinkedList<Integer>[] tmp = new LinkedList[5];
				for(int i=0;i<5;i++) tmp[i] = new LinkedList<Integer>(cups[i]);
				tmp[1].add(tmp[2].removeLast());
				cnt = solve(tmp, c, 2, 1);
				cups[3].add(cups[2].removeLast());
				cnt = min(cnt, solve(cups, c, 2, 1));
			}
			else {
				cnt = solve(cups, -1, -1, 0);
			}
			if( cnt <= m ) System.out.println(cnt);
			else System.out.println(-1);
		}
	}

	int solve(LinkedList<Integer>[] cups, int pastp, int pasti, int cp) {
		int cnt;
		int p, i;

		for(cnt=cp;cnt<=m;cnt++) {
			i = 0;
			p = 20;
			if( cups[1].size() == n+1 || cups[3].size() == n+1 ) {
				break;
			}
			int from = 0;

			for(int j=1;j<=3;j++) {
				int left = cups[j-1].getLast();
				int right = cups[j+1].getLast();
				int c = cups[j].getLast();
//				debug(left, right, c);
				if( p < c || c == 0 || ( c == 1 && ( j == 1 || j == 3 ) ) ) continue;
				if( c != pastp && c > left ) {
					p = c;
					i = j-1;
					from = j;
				}
				else if( c != pastp && c > right ) {
					p = c;
					i = j+1;
					from = j;
				}
				else if( c == pastp && c > left && pasti != j-1 ) {
					p = c;
					i = j-1;
					from = j;
				}
				else if( c == pastp && c > right && pasti != j+1 ) {
					p = c;
					i = j+1;
					from = j;
				}
				else continue;
//				debug("    ", p, i, from);
//				debug("    ", pasti, from);
			}
			cups[i].addLast( cups[from].removeLast() );
			pastp = p;
			pasti = from;
//			for(LinkedList<Integer> a: cups) debug(a);
		}
		return cnt;
	}

	public static void main(String[] args) {
		new Main().run();
	}

	void debug(Object... os) {
		System.err.println(Arrays.deepToString(os));
	}
}

import java.util.*;
import static java.lang.System.*;

public class Main {

	Scanner sc = new Scanner(in);

	long[][][][] memo = new long[3][16][16][16];
	int[] cups;
	int n;

	long calcA(int a, int b,int c) {
		if (memo[0][a][b][c] == 0) {
			if (c == 0 && b == 0) {
				return 0;
			}
			if (b == 0 && a == 0) {
				return memo[0][a][b][c] = (long)(Math.pow(3, c)-1);
			}
			int mp = cups[n-a-b-c];
			if (mp == 2) {
				memo[0][a][b][c] = calcA(a, b, c-1) + calcC(a+b+c-1, 0, 0) + calcA(0, 0, a+b+c-1) + 2;
			} else if (mp == 1) {
				memo[0][a][b][c] = calcC(a, b-1, c) + calcA(0, 0, a+b+c-1) + 1;
			} else {
				memo[0][a][b][c] = calcA(a-1, b, c);
			}
		}
		return memo[0][a][b][c];
	}

	long calcB(int a, int b,int c) {
		if (memo[1][a][b][c] == 0) {
			if (a == 0 && c == 0) {
				return 0;
			}
			if ((a == 0 && b == 0) || (b == 0 && c == 0)) {
				return memo[1][a][b][c] = (long)(Math.pow(3, a+c)-3)/2 + 1;
			}
			int mp = cups[n-a-b-c];
			if (mp == 0) {
				memo[1][a][b][c] = calcC(a-1, b, c) + calcB(0, 0, a+b+c-1) + 1;
			} else if (mp == 1) {
				memo[1][a][b][c] = calcB(a, b-1, c);
			} else {
				memo[1][a][b][c] = calcA(a, b, c-1) + calcB(a+b+c-1, 0, 0) + 1;
			}
		}
		return memo[1][a][b][c];
	}

	long calcC(int a, int b, int c) {
		if (memo[2][a][b][c] == 0) {
			if (a == 0 && b == 0) {
				return 0;
			}
			if (b == 0 && c == 0) {
				return memo[2][a][b][c] = (long)(Math.pow(3, a)-1);
			}
			int mp = cups[n-a-b-c];
			if (mp == 0) {
				memo[2][a][b][c] = calcC(a-1, b, c) + calcA(0, 0, a+b+c-1) + calcC(a+b+c-1, 0, 0) + 2;
			} else if (mp == 1) {
				memo[2][a][b][c] = calcA(a, b-1, c) + calcC(a+b+c-1, 0, 0) + 1;
			} else {
				memo[2][a][b][c] = calcC(a, b, c-1);
			}
		}
		return memo[2][a][b][c];
	}

	void run() {
		while (true) {
			n = sc.nextInt();
			int m = sc.nextInt();
			if (n == 0 && m == 0) break;

			cups = new int[n];
			int a = 0, b = 0, c = 0;
			for (int i = 0; i < 3; i++) {
				int num = sc.nextInt();
				if (i == 0) a = num;
				else if (i == 1) b = num;
				else c = num;
				for (int j = 0; j < num; j++) {
					cups[sc.nextInt()-1] = i;
				}
			}
			long res = Math.min(calcA(a, b, c), calcC(a, b, c));
			if (res <= m)
				out.println(res);
			else
				out.println(-1);
		}
	}

	public static void main(String[] args) {
		new Main().run();
	}

}

