//Cup
public class Main{

	public static int n;
	public static Stack<Integer>[] cup;
	public static int ans;

	//ope
	//1: A->B
	//2: C->B
	//-1:B->A
	//-2:B->C
	public static void dfs(int pre, int k){
		if(ans <= k)return;
		if(cup[0].size()==n || cup[2].size()==n){
			ans = Math.min(ans, k);
			return;
		}
		if(pre!=1){
			if(!cup[1].empty()){
				if(cup[0].empty() || cup[0].peek() < cup[1].peek()){
					cup[0].push(cup[1].pop());
					dfs(-1, k+1);
					cup[1].push(cup[0].pop());
				}
			}
		}
		if(pre!=-1){
			if(!cup[0].empty()){
				if(cup[1].empty() || cup[1].peek() < cup[0].peek()){
					cup[1].push(cup[0].pop());
					dfs(1, k+1);
					cup[0].push(cup[1].pop());
				}
			}
		}
		if(pre!=2){
			if(!cup[1].empty()){
				if(cup[2].empty() || cup[2].peek() < cup[1].peek()){
					cup[2].push(cup[1].pop());
					dfs(-2, k+1);
					cup[1].push(cup[2].pop());
				}
			}
		}
		if(pre!=-2){
			if(!cup[2].empty()){
				if(cup[1].empty() || cup[1].peek() < cup[2].peek()){
					cup[1].push(cup[2].pop());
					dfs(2, k+1);
					cup[2].push(cup[1].pop());
				}
			}
		}
	}

	@SuppressWarnings("unchecked")
	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		while(true){
			n = sc.nextInt();
			int m = sc.nextInt();
			if(n==0&&m==0)break;
			ans = m+1;
			cup = new Stack[3];
			for(int i=0;i<3;i++)cup[i] = new Stack<Integer>();
			for(int i=0;i<3;i++){
				int k = sc.nextInt();
				while(k--!=0)cup[i].push(sc.nextInt());
			}
			dfs(0, 0);
			System.out.println(ans==m+1?-1:ans);
		}
	}
}

import java.util.ArrayList;
import java.util.Arrays;
import java.util.LinkedList;
import java.util.Scanner;

public class Main {

	public static final int MAX = 15;

	public static int[][] lists = new int[3][MAX];
	public static int[]   sizes = new int[3];

	public static int[][] prevs = new int[3][MAX];
	public static int[] p_sizes = new int[3];

	public static void lists_clear(){
		sizes[0] = sizes[1] = sizes[2] = 0;
	}

	public static void prevs_clear(){
		p_sizes[0] = p_sizes[1] = p_sizes[2] = 0;
	}

	public static void all_clear(){
		lists_clear();
		prevs_clear();
	}

	public static void print_list(int l){
		System.out.print(l + "  :");
		for(int i = 0; i < sizes[l]; i++){
			System.out.print(" " + lists[l][i]);
		}
		System.out.println();
	}

	public static void print_prev_list(int l){
		System.out.print(l + " p:");
		for(int i = 0; i < p_sizes[l]; i++){
			System.out.print(" " + prevs[l][i]);
		}
		System.out.println();
	}

	public static boolean range_check(int i){
		return 0 <= i && i < 3;
	}

	public static boolean is_goal(int n){
		return sizes[0] == n && sizes[1] == 0 && sizes[2] == 0 || sizes[0] == 0 && sizes[1] == 0 && sizes[2] == n;
	}

	public static int get_list_top(int i){
		if(!range_check(i)){
			return 0;
		}else if(sizes[i] == 0){
			return 0;
		}else{
			return lists[i][sizes[i] - 1];
		}
	}

	public static void save_to_prev(){
		for(int i = 0; i < 3; i++){
			p_sizes[i] = sizes[i];

			for(int j = 0; j < sizes[i]; j++){
				prevs[i][j] = lists[i][j];
			}
		}
	}

	public static boolean is_same(){
		for(int i = 0; i < 3; i++){
			if(sizes[i] != p_sizes[i]){
				return false;
			}

			for(int j = 0; j < sizes[i]; j++){
				if(lists[i][j] != prevs[i][j]){
					return false;
				}
			}
		}

		return true;
	}

	public static void add_top(int l, int t){
		lists[l][sizes[l]] = t;
		sizes[l]++;
	}

	public static int remove_top(int l){
		int tmp = get_list_top(l);
		sizes[l]--;

		return tmp;
	}

	public static int move(int cur, boolean left, boolean do_it){
		if(!range_check(cur)){
			return 0;
		}else if(sizes[cur] == 0){
			return 0;
		}

		final int pur = cur + (left ? -1 : 1);
		if(!range_check(pur)){
			return 0;
		}

		final int cur_top = get_list_top(cur);
		final int pur_top = get_list_top(pur);

		if(cur_top <= pur_top){
			return 0;
		}

		boolean same = false;
		if(do_it){
			save_to_prev();
		}
		add_top(pur, remove_top(cur));

		if(do_it){
			return Integer.MAX_VALUE;
		}

		if(is_same()){
			same = true;
		}

		add_top(cur, remove_top(pur));

		if(same){
			return 0;
		}else{
			return cur_top;
		}
	}

	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);

		while (true) {
			final int n = sc.nextInt();
			final int m = sc.nextInt();

			if(n == 0 && m == 0){
				break;
			}

			all_clear();

			for(int i = 0; i < 3; i++){
				sizes[i] = sc.nextInt();

				for(int j = 0; j < sizes[i]; j++){
					lists[i][j] = sc.nextInt();
				}
			}

			boolean clear = false;
			int count = 0;

			for(count = 0; count <= m; count++){
				/*
				System.out.println(count + " : --------------------");
				print_list(0);
				print_prev_list(0);
				print_list(1);
				print_prev_list(1);
				print_list(2);
				print_prev_list(2);
				*/

				if(is_goal(n)){
					clear = true;
					break;
				}

				boolean min_left = false;
				int min_cur = 0;
				int min = Integer.MAX_VALUE;

				for(int left = 0; left < 2; left++){
					for(int cur = 0; cur < 3; cur++){
						int ret = move(cur, left == 0, false);

						if(ret != 0 && ret < min){
							min = ret;
							min_cur = cur;
							min_left = left == 0;
						}
					}
				}

				if(min != Integer.MAX_VALUE){
					move(min_cur, min_left, true);
				}else{
					System.out.println("ERROR");
				}
			}


			System.out.println(clear ? count + "" : "-1");
		}
	}

}

import java.util.*;
import static java.lang.System.*;

public class Main {

	Scanner sc = new Scanner(in);

	long[][][][] memo = new long[3][16][16][16];
	int[] cups;
	int n;

	long calcA(int a, int b,int c) {
		if (memo[0][a][b][c] == 0) {
			if (c == 0 && b == 0) {
				return 0;
			}
			if (b == 0 && a == 0) {
				return memo[0][a][b][c] = (long)(Math.pow(3, c)-1);
			}
			int mp = cups[n-a-b-c];
			if (mp == 2) {
				memo[0][a][b][c] = calcA(a, b, c-1) + calcC(a+b+c-1, 0, 0) + calcA(0, 0, a+b+c-1) + 2;
			} else if (mp == 1) {
				memo[0][a][b][c] = calcC(a, b-1, c) + calcA(0, 0, a+b+c-1) + 1;
			} else {
				memo[0][a][b][c] = calcA(a-1, b, c);
			}
		}
		return memo[0][a][b][c];
	}

	long calcB(int a, int b,int c) {
		if (memo[1][a][b][c] == 0) {
			if (a == 0 && c == 0) {
				return 0;
			}
			if ((a == 0 && b == 0) || (b == 0 && c == 0)) {
				return memo[1][a][b][c] = (long)(Math.pow(3, a+c)-3)/2 + 1;
			}
			int mp = cups[n-a-b-c];
			if (mp == 0) {
				memo[1][a][b][c] = calcC(a-1, b, c) + calcB(0, 0, a+b+c-1) + 1;
			} else if (mp == 1) {
				memo[1][a][b][c] = calcB(a, b-1, c);
			} else {
				memo[1][a][b][c] = calcA(a, b, c-1) + calcB(a+b+c-1, 0, 0) + 1;
			}
		}
		return memo[1][a][b][c];
	}

	long calcC(int a, int b, int c) {
		if (memo[2][a][b][c] == 0) {
			if (a == 0 && b == 0) {
				return 0;
			}
			if (b == 0 && c == 0) {
				return memo[2][a][b][c] = (long)(Math.pow(3, a)-1);
			}
			int mp = cups[n-a-b-c];
			if (mp == 0) {
				memo[2][a][b][c] = calcC(a-1, b, c) + calcA(0, 0, a+b+c-1) + calcC(a+b+c-1, 0, 0) + 2;
			} else if (mp == 1) {
				memo[2][a][b][c] = calcA(a, b-1, c) + calcC(a+b+c-1, 0, 0) + 1;
			} else {
				memo[2][a][b][c] = calcC(a, b, c-1);
			}
		}
		return memo[2][a][b][c];
	}

	void run() {
		while (true) {
			n = sc.nextInt();
			int m = sc.nextInt();
			if (n == 0 && m == 0) break;

			cups = new int[n];
			int a = 0, b = 0, c = 0;
			for (int i = 0; i < 3; i++) {
				int num = sc.nextInt();
				if (i == 0) a = num;
				else if (i == 1) b = num;
				else c = num;
				for (int j = 0; j < num; j++) {
					cups[sc.nextInt()-1] = i;
				}
			}
			long res = Math.min(calcA(a, b, c), calcC(a, b, c));
			if (res <= m)
				out.println(res);
			else
				out.println(-1);
		}
	}

	public static void main(String[] args) {
		new Main().run();
	}

}

import java.util.Arrays;
import java.util.Scanner;

public class Main {

	static Scanner sc = new Scanner(System.in);
	static int N, M;
	static int[] p = new int[15];
	static int[] move = new int[15];

	static int solve() {
		int[][] dp = new int[N + 1][3];
		for (int i = 0; i < N; ++i) {
			Arrays.fill(dp[i + 1], Integer.MAX_VALUE);
		}
		for (int i = 0; i < N; ++i) {
			for (int j = 0; j < 3; ++j) {
				dp[i + 1][p[i]] = Math.min(dp[i + 1][p[i]], dp[i][j] + move[i] * Math.abs(j - p[i]));
			}
			if (p[i] == 1) {
				dp[i + 1][0] = Math.min(dp[i + 1][0], dp[i][0] + move[i] * 4 + 1);
				dp[i + 1][0] = Math.min(dp[i + 1][0], dp[i][1] + move[i] * 3 + 1);
				dp[i + 1][0] = Math.min(dp[i + 1][0], dp[i][2] + move[i] * 2 + 1);
				dp[i + 1][2] = Math.min(dp[i + 1][2], dp[i][2] + move[i] * 4 + 1);
				dp[i + 1][2] = Math.min(dp[i + 1][2], dp[i][1] + move[i] * 3 + 1);
				dp[i + 1][2] = Math.min(dp[i + 1][2], dp[i][0] + move[i] * 2 + 1);
			} else {
				dp[i + 1][1] = Math.min(dp[i + 1][1], dp[i][p[i]] + move[i] * 3 + 1);
				dp[i + 1][1] = Math.min(dp[i + 1][1], dp[i][1] + move[i] * 2 + 1);
				dp[i + 1][1] = Math.min(dp[i + 1][1], dp[i][2 - p[i]] + move[i] * 1 + 1);
				dp[i + 1][2 - p[i]] = Math.min(dp[i + 1][2 - p[i]], dp[i][p[i]] + move[i] * 6 + 2);
				dp[i + 1][2 - p[i]] = Math.min(dp[i + 1][2 - p[i]], dp[i][1] + move[i] * 5 + 2);
				dp[i + 1][2 - p[i]] = Math.min(dp[i + 1][2 - p[i]], dp[i][2 - p[i]] + move[i] * 4 + 2);
			}
		}
		int m = Math.min(dp[N][0], dp[N][2]);
		return m <= M ? m : -1;
	}

	public static void main(String[] args) throws Exception {
		for (int i = 1; i < move.length; ++i) {
			move[i] = move[i - 1] * 3 + 1;
		}
		while (true) {
			N = sc.nextInt();
			if (N == 0) break;
			M = sc.nextInt();
			for (int i = 0; i < 3; ++i) {
				int c = sc.nextInt();
				for (int j = 0; j < c; ++j) {
					p[N - sc.nextInt()] = i;
				}
			}
			System.out.println(solve());
		}
	}
}

