  public static int calc_sub(int cnt,int max,Stack<int>a,Stack<int> b,Stack<int> c, List<int[][]> met) {
	  if((a.Count==0) && (b.Count==0)) return cnt;
	  if((b.Count==0) && (c.Count==0)) return cnt;
	  if(max<cnt) return max+10;

