	    Stack<int> aa = CloneStack<int>(a);
	    Stack<int> bb = CloneStack<int>(b);
	    bb.Push(aa.Pop());

		m1.Add(new int[][]{aa.ToArray(),bb.ToArray(),c.ToArray()});
		t1 = calc_sub(cnt+1, max, aa, bb, c, m1);
	  }

	  //b->a
	  if((0<b.Count) && ((a.Count==0) || (a.Peek() < b.Peek()))) {
	    Stack<int> aa = CloneStack<int>(a);
	    Stack<int> bb = CloneStack<int>(b);
	    aa.Push(bb.Pop());

		m2.Add(new int[][]{aa.ToArray(),bb.ToArray(),c.ToArray()});
		t2 = calc_sub(cnt+1, max, aa, bb, c, m2);
	  }

	  //b->c
	  if((0<b.Count) && ((c.Count==0) || (c.Peek() < b.Peek()))) {
	    Stack<int> bb = CloneStack<int>(b);
	    Stack<int> cc = CloneStack<int>(c);
	    cc.Push(bb.Pop());

		m3.Add(new int[][]{a.ToArray(),bb.ToArray(),cc.ToArray()});
		t3 = calc_sub(cnt+1, max, a, bb, cc, m3);
	  }

	  //c->b
	  if((0<c.Count) && ((b.Count==0) || (b.Peek() < c.Peek()))) {
	    Stack<int> bb = CloneStack<int>(b);
	    Stack<int> cc = CloneStack<int>(c);
	    bb.Push(cc.Pop());

		m4.Add(new int[][]{a.ToArray(),bb.ToArray(),cc.ToArray()});
		t4 = calc_sub(cnt+1, max, a, bb, cc, m4);
	  }

	  mincnt = Math.Min(Math.Min(t1,t2),Math.Min(t3,t4));
	  if(t1==mincnt) { met.AddRange(m1); return mincnt; }
	  if(t2==mincnt) { met.AddRange(m2); return mincnt; }
	  if(t3==mincnt) { met.AddRange(m3); return mincnt; }
	  if(t4==mincnt) { met.AddRange(m4); return mincnt; }

	  return mincnt;
  }

    public static Stack<T> CloneStack<T>(Stack<T> original)
    {
        var arr = new T[original.Count];
        original.CopyTo(arr, 0);
        Array.Reverse(arr);
        return new Stack<T>(arr);
    }






