          csa.Method.Add(1);
          acsl.Add(csa);
	    }
        //b->a
	    if((mode != 1) && (0<=vb) && (va < vb)) {
	      CupState csa = new CupState();
          csa.B = new byte[ccs.B.Length-1];
          Array.Copy(ccs.B,0,csa.B,0,ccs.B.Length-1);
          csa.A = new byte[ccs.A.Length+1];
          Array.Copy(ccs.A,0,csa.A,0,ccs.A.Length);
          csa.A[csa.A.Length-1] = ccs.B[ccs.B.Length-1];
          csa.C = ccs.C;
          csa.Method = new List<byte>(ccs.Method.ToArray());
          csa.Method.Add(2);
          acsl.Add(csa);
	    }
        //b->c
	    if((mode != 4) && (0<=vb) && (vc < vb)) {
	      CupState csa = new CupState();
          csa.B = new byte[ccs.B.Length-1];
          Array.Copy(ccs.B,0,csa.B,0,ccs.B.Length-1);
          csa.C = new byte[ccs.C.Length+1];
          Array.Copy(ccs.C,0,csa.C,0,ccs.C.Length);
          csa.C[csa.C.Length-1] = ccs.B[ccs.B.Length-1];
          csa.A = ccs.A;
          csa.Method = new List<byte>(ccs.Method.ToArray());
          csa.Method.Add(3);
          acsl.Add(csa);
	    }
        //c->b
	    if((mode != 3) && (0<=vc) && (vb < vc)) {
	      CupState csa = new CupState();
          csa.C = new byte[ccs.C.Length-1];
          Array.Copy(ccs.C,0,csa.C,0,ccs.C.Length-1);
          csa.B = new byte[ccs.B.Length+1];
          Array.Copy(ccs.B,0,csa.B,0,ccs.B.Length);
          csa.B[csa.B.Length-1] = ccs.C[ccs.C.Length-1];
          csa.A = ccs.A;
          csa.Method = new List<byte>(ccs.Method.ToArray());
          csa.Method.Add(4);
          acsl.Add(csa);
	    }
	  }
      mcsl = acsl;

	}

	return (ccsl.Count == 0) ? -1 : mincnt;
  }


  public static int calc_sub_1(int cnt,int max,Stack<byte>a,Stack<byte> b,Stack<byte> c, byte mode) {
	  if((a.Count==0) && (b.Count==0)) return cnt;
	  if((b.Count==0) && (c.Count==0)) return cnt;
	  if(max<cnt) return -1;

	  int mincnt = max;
	  bool suc = false;

	  //a->b
	  if((mode != 2) && (0<a.Count) && ((b.Count==0) || (b.Peek() < a.Peek()))) {
	    b.Push(a.Pop());
		int t = calc_sub_1(cnt+1, mincnt, a, b, c, 1);
		a.Push(b.Pop());
		if ((0<=t)&&(t<mincnt)) mincnt = t;
		if(0<=t) suc = true;
	  }

	  //b->a
	  if((mode != 1) && (0<b.Count) && ((a.Count==0) || (a.Peek() < b.Peek()))) {
	    a.Push(b.Pop());
		int t = calc_sub_1(cnt+1, mincnt, a, b, c, 2);
		b.Push(a.Pop());
		if ((0<=t)&&(t<mincnt)) mincnt = t;
		if(0<=t) suc = true;
	  }

	  //b->c
	  if((mode != 4) && (0<b.Count) && ((c.Count==0) || (c.Peek() < b.Peek()))) {
	    c.Push(b.Pop());
		int t = calc_sub_1(cnt+1, mincnt, a, b, c, 3);
		b.Push(c.Pop());
		if ((0<=t)&&(t<mincnt)) mincnt = t;
		if(0<=t) suc = true;
	  }

	  //c->b
	  if((mode != 3) && (0<c.Count) && ((b.Count==0) || (b.Peek() < c.Peek()))) {
	    b.Push(c.Pop());
		int t = calc_sub_1(cnt+1, mincnt, a, b, c, 4);
		c.Push(b.Pop());
		if ((0<=t)&&(t<mincnt)) mincnt = t;
		if(0<=t) suc = true;
	  }

	  if(suc) {
	    return mincnt;
	  } else {
		return -1;
	  }

  }

  public static int calc_sub_dbg(int cnt,int max,Stack<byte>a,Stack<byte> b,Stack<byte> c, List<byte[][]> met) {
	  if((a.Count==0) && (b.Count==0)) return cnt;
	  if((b.Count==0) && (c.Count==0)) return cnt;
	  if(max<cnt) return max+10;

	  int mincnt = max+10;
	  List<byte[][]> m1 = new List<byte[][]>();
	  List<byte[][]> m2 = new List<byte[][]>();
	  List<byte[][]> m3 = new List<byte[][]>();
	  List<byte[][]> m4 = new List<byte[][]>();
	  int t1 = mincnt;
	  int t2 = mincnt;
	  int t3 = mincnt;
	  int t4 = mincnt;

	  //a->b
	  if((0<a.Count) && ((b.Count==0) || (b.Peek() < a.Peek()))) {
	    Stack<byte> aa = CloneStack<byte>(a);
	    Stack<byte> bb = CloneStack<byte>(b);
	    bb.Push(aa.Pop());

		//m1.Add(new byte[][]{aa.ToArray(),bb.ToArray(),c.ToArray()});
		t1 = calc_sub_dbg(cnt+1, max, aa, bb, c, m1);
	  }

	  //b->a
	  if((0<b.Count) && ((a.Count==0) || (a.Peek() < b.Peek()))) {
	    Stack<byte> aa = CloneStack<byte>(a);
	    Stack<byte> bb = CloneStack<byte>(b);
	    aa.Push(bb.Pop());

		//m2.Add(new byte[][]{aa.ToArray(),bb.ToArray(),c.ToArray()});
		t2 = calc_sub_dbg(cnt+1, max, aa, bb, c, m2);
	  }

	  //b->c
	  if((0<b.Count) && ((c.Count==0) || (c.Peek() < b.Peek()))) {
	    Stack<byte> bb = CloneStack<byte>(b);
	    Stack<byte> cc = CloneStack<byte>(c);
	    cc.Push(bb.Pop());

		//m3.Add(new byte[][]{a.ToArray(),bb.ToArray(),cc.ToArray()});
		t3 = calc_sub_dbg(cnt+1, max, a, bb, cc, m3);
	  }

	  //c->b
	  if((0<c.Count) && ((b.Count==0) || (b.Peek() < c.Peek()))) {
	    Stack<byte> bb = CloneStack<byte>(b);
	    Stack<byte> cc = CloneStack<byte>(c);
	    bb.Push(cc.Pop());

		//m4.Add(new byte[][]{a.ToArray(),bb.ToArray(),cc.ToArray()});
		t4 = calc_sub_dbg(cnt+1, max, a, bb, cc, m4);
	  }

	  mincnt = Math.Min(Math.Min(t1,t2),Math.Min(t3,t4));
	  if(t1==mincnt) { met.AddRange(m1); return mincnt; }
	  if(t2==mincnt) { met.AddRange(m2); return mincnt; }
	  if(t3==mincnt) { met.AddRange(m3); return mincnt; }
	  if(t4==mincnt) { met.AddRange(m4); return mincnt; }

	  return mincnt;
  }

    public static Stack<T> CloneStack<T>(Stack<T> original)
    {
        var arr = new T[original.Count];
        original.CopyTo(arr, 0);
        Array.Reverse(arr);
        return new Stack<T>(arr);
    }






