const int INF = 1 << 24;
const int dx[4] = {0, 0, 1, -1}, dy[4] = {1, -1, 0, 0};

typedef unsigned long long ull;

int N, R, w, h;
int R_2[10001][10001];
std::vector<int> X1, X2, Y1, Y2, X, Y;

int compress(std::vector<int>& v1, std::vector<int>& v2, std::vector<int>& v, int w){
    REP(i, N){
        FOR(j, -1, 2){
            int s = v1[i] + j, t = v2[i] + j;
            if(0 <= s && s <= 10000){
                v.push_back(s);
            }
            if(0 <= t && t <= 10000){
                v.push_back(t);
            }
        }
    }

    std::sort(v.begin(), v.end());
    v.erase(std::unique(v.begin(), v.end()), v.end()); 

    REP(i, N){
        v1[i] = std::lower_bound(v.begin(), v.end(), v1[i]) - v.begin();
        v2[i] = std::lower_bound(v.begin(), v.end(), v2[i]) - v.begin();
    }

    return v.size();
}

int solve_S(){
    int res = 0;
    REP(i, h){
        REP(j, w){
            if(R_2[j][i] > 0){
                int dX = std::abs(X[j] - X[j+1]), dY = std::abs(Y[i] - Y[i+1]);
                res += dX * dY;
            }
        }
    }

    return res;
}

int solve_L(){
    int res = 0;
    FOR(i, -1, h+1){
        FOR(j, -1, w+1){
            if((i == -1 || j == -1 || i == h || j == w) || R_2[j][i] == 0){
                REP(k, 4){
                    int x = j + dx[k], y = i + dy[k];
                    if(0 <= x && x < w && 0 <= y && y < h && R_2[x][y] > 0){
                        if(dx[k] != 0){
                            res += std::abs(Y[y] - Y[y+1]);
                        }else{
                            res += std::abs(X[x] - X[x+1]);
                        }
                    }
                }
            }
        }
    }

    return res;
}

int main(){
    while(std::cin >> N >> R, N){
        X1.clear();
        X2.clear();
        Y1.clear();
        Y2.clear();
        X.clear();
        Y.clear();

        REP(i, N){
            int x1, y1, x2, y2;
            std::cin >> x1 >> y1 >> x2 >> y2;

            X1.push_back(x1);
            X2.push_back(x2);
            Y1.push_back(y1);
            Y2.push_back(y2);
        }

        w = compress(X1, X2, X, N);
        h = compress(Y1, Y2, Y, N);

        REP(i, h){
            REP(j, w){
                R_2[j][i] = 0;
            }
        }

        REP(i, N){
            R_2[X1[i]][Y1[i]]++;
            R_2[X1[i]][Y2[i]]--;
            R_2[X2[i]][Y1[i]]--;
            R_2[X2[i]][Y2[i]]++;
        }

        // X
        REP(i, h){
            FOR(j, 1, w){
                R_2[j][i] += R_2[j-1][i];
            }
        }

        // Y
        REP(i, h){
            FOR(j, 1, w){
                R_2[i][j] += R_2[i][j-1];
            }
        }

        // std::cout << w << "," << h << std::endl;
        // REP(i, 10){
        //     REP(j, 10){
        //         printf("%d, ", R_2[j][i]);
        //     }
        //     puts("");
        // }

        if(R == 1){
            std::cout << solve_S() << std::endl;
        }else{
            std::cout << solve_S() << std::endl;
            std::cout << solve_L() << std::endl;
        }
    }
}

