                int yMin = a * 1000 - 1;
                int yMax = (a+1) * 1000 - 1;
                int xMin = b * 1000 - 1;
                int xMax = (b+1) * 1000 - 1;

                if(gy < yMin || gx < xMin || sy > yMax || sx > yMax)
                    continue;

                vector<vector<int> > s(1001, vector<int>(1001, 0));
                for(int i=0; i<n; ++i){
