                        continue;

                    ++ s[max(yMin, y1[i])-yMin][max(xMin, x1[i])-xMin];
                    if(x2[i] <= xMax)
                        -- s[max(yMin, y1[i])-yMin][x2[i]-xMin];
                    if(y2[i] <= yMax)
                        -- s[y2[i]-yMin][max(xMin, x1[i])-xMin];
                    if(y2[i] <= yMax && x2[i] <= xMax)
                        ++ s[y2[i]-yMin][x2[i]-xMin];
                }

                for(int i=0; i<1001; ++i){
                    for(int j=0; j<1000; ++j){
                        s[i][j+1] += s[i][j];
                    }
                }
                for(int i=0; i<1001; ++i){
                    for(int j=0; j<1000; ++j){
                        s[j+1][i] += s[j][i];
                    }
                }

                for(int i=0; i<1000; ++i){
                    for(int j=0; j<1000; ++j){
                        if(s[i][j] > 0)
                            ++ ret1;
                        if((s[i][j] > 0) ^ (s[i+1][j] > 0))
                            ++ ret2;
                        if((s[i][j] > 0) ^ (s[i][j+1] > 0))
                            ++ ret2;
                    }
                }
            }
        }

        cout << ret1 << endl;
        if(r == 2)
            cout << ret2 << endl;
    }
}

