#include <algorithm>
#include <iostream>
#include <vector>
using namespace std;

#define ALL(c) begin(c), end(c)

int main() {
  cin.tie(0);
  ios_base::sync_with_stdio(false);

  int n, r;
  while (cin >> n >> r, n | r) {
    vector<int> x1(n), y1(n), x2(n), y2(n);
    for (int i = 0; i < n; ++i) {
      cin >> x1[i] >> y1[i] >> x2[i] >> y2[i];
      ++x1[i]; ++y1[i]; ++x2[i]; ++y2[i];
    }
    int W = *max_element(ALL(x2)) + 1;
    int H = *max_element(ALL(y2)) + 1;

    vector<vector<bool>> field(H, vector<bool>(W, false));
    for (int i = 0; i < n; ++i)
      for (int y = y1[i]; y < y2[i]; ++y)
        fill(begin(field[y]) + x1[i], begin(field[y]) + x2[i], true);

    if (r == 1) {
      int ans = 0;
      for (int y = 0; y < H; ++y)
        ans += count(ALL(field[y]), true);
      cout << ans << endl;
    } else {
      int ansS = 0, ansL = 0;
      for (int y = 1; y < H; ++y) {
        for (int x = 1; x < W; ++x) {
          int b = field[y][x];
          ansS += b;
          ansL += (field[y][x-1] ^ b) + (field[y-1][x] ^ b);
        }
      }
      cout << ansS << endl << ansL << endl;
    }
  }
}

