  cout << "sizeof: " << sizeof(arr)/sizeof(arr[0]) << endl;
  for (int i = 1; i < sizeof(arr); i++) {
    if (max < arr[i]) max = arr[i];
  }
  return max;
}

int min(int* arr) {
  int min = arr[0];
  for (int i = 1; i < sizeof(arr); i++) {
    if (min > arr[i]) min = arr[i];
  }
  return min;
}

