	//cout << c[i]<<" " << c[i]<<" " << nowh<<" " << nowl << endl;
	area+=(nowh-nowl)*width;
      }else if (nowl == h){
	//cout << c[i]<<" " << c[i]<<" " << nowh<<" " << nowl << endl;
	area+=(nowh-nowl)*width;
      }else if (nowh > h){
	//cout << c[i]<<" " << c[i]<<" " << nowh<<" " << h << endl;
	area+=(nowh-h)*width;
      }
      l=nowl;
      h=max(h,nowh);
    }
  }
  return mp(area,len);
}

