#include <cstdio>
#include <vector>
#include <algorithm>

#define rep(i, n) for(int i = 0; i < (n); ++i)

using namespace std;

struct R{
	R(){}
	R(int a, int b, int c, int d): a(a), b(b), c(c), d(d){}

	int a, b, c, d;
};

int n, r;
vector<R> v;

R rect_intersect(const R& x, const R& y){
	return R(max(x.a, y.a), max(x.b, y.b), min(x.c, y.c), min(x.d, y.d));
}

bool rect_empty(const R& x){
	return x.a >= x.c || x.b >= x.d;
}

bool rect_cmp(const R& x, const R& y){
	return x.a == y.a && x.b == y.b && x.c == y.c && x.d == y.d;
}

int rect_area(const R& x){
	return (x.c - x.a) * (x.d - x.b);
}

int area(const vector<R>& v, const R& x){
	rep(i, v.size()){
		if(rect_cmp(v[i], x)){
			return rect_area(x);
		}
	}

	int p = (x.a + x.c) / 2;
	int q = (x.b + x.d) / 2;

	R y[4] = {
		R(x.a, x.b, p, q),
		R(x.a, q, p, x.d),
		R(p, x.b, x.c, q),
		R(p, q, x.c, x.d)
	};
	vector<R> a[4];
	rep(i, v.size()){
		rep(j, 4){
			R t = rect_intersect(v[i], y[j]);
			if(!rect_empty(t)){
				a[j].push_back(t);
			}
		}
	}

	int r = 0;
	rep(j, 4){
		if(!a[j].empty()){
			r += area(a[j], y[j]);
		}
	}

	return r;
}

int main(){
	while(1){
		scanf("%d%d", &n, &r);
		if(n == 0){
			break;
		}
		rep(i, n){
			R p;
			scanf("%d%d%d%d", &p.a, &p.b, &p.c, &p.d);
			v.push_back(p);
		}

		printf("%d\n", area(v, R(0, 0, 10000, 10000)));
		v.clear();
	}
	return 0;
}

