	for(int k = 0; k < (int)x[j].size(); k++){
	  if(x[j][k].F <= y2 && x[j][k].S >= y1){
	    x[j][k].F = min(x[j][k].F,y1);
	    x[j][k].S = max(x[j][k].S,y2);
	    f = false;
