	    for(int k = 0; k < (int)xx[i-1].size(); k++){
	      if(xx[i][j].F < xx[i-1][k].S && xx[i][j].S > xx[i-1][k].F){
		res -= min(xx[i][j].S,xx[i-1][k].S) - max(xx[i][j].F,xx[i-1][k].F);
	      }
	    }	
	  }  
	  if(i+1 < 10000){
