#define S_ second

typedef long long int lli;
typedef pair<int,int> P;

const int INF=1<<30;
const long long int INF_=1LL<<58;
const int dx[]={1,0,-1,0},dy[]={0,1,0,-1};

int min_x,min_y,max_x,max_y,W,H;
unsigned char plane[10001][10001];

inline void Imos(){
	REP(y,0,H){
		REP(x,1,W){
			plane[x][y]+=plane[x-1][y];
		}
	}
	REP(x,0,W){
		for(int y=H-2; y>=0; --y){
			plane[x][y]+=plane[x][y+1];
		}
	}
}

inline int CalcSize(/*vector<vector<unsigned char> > &plane*/){
	int result=0;
	REP(x,0,W){
		REP(y,0,H){
			if(plane[x][y]>0) ++result;
		}
	}
	return result;
}

inline int BFS(vector<vector<bool> > &has_done,int x,int y){
	int result=0;
	queue<P> que;
	que.push(MP(x,y));
	has_done[x][y]=true;
	while(!que.empty()){
		P p=que.front();
		que.pop();
		REP(i,0,4){
			int nx=p.F_+dx[i],ny=p.S_+dy[i];	
			if(0<=nx&&nx<W&&0<=ny&&ny<H&&!has_done[nx][ny]){
				if(plane[nx][ny]>0){
					que.push(MP(nx,ny));
					has_done[nx][ny]=true;
				}else ++result;
			}
		}
	}
	return result;
}

inline int CalcLength(int n,vector<int> &X,vector<int> &Y){
	vector<vector<bool> > has_done(W,vector<bool>(H));
	int result=0;
	/*
	REP(x,0,plane.size()){
		REP(y,0,plane[0].size()){
			if(!has_done[x][y]&&plane[x][y]>0){
				result+=BFS(plane,has_done,x,y,plane.size(),plane[0].size());
			}
		}
	}
	*/
	REP(i,0,n){
		if(!has_done[X[i]][Y[i]]&&plane[X[i]][Y[i]]>0) result+=BFS(has_done,X[i],Y[i]);
	}
	return result;
}

int main(){
	std::ios::sync_with_stdio(false);
	std::cin.tie(0);
	int N,R;
	while(cin >> N >> R&&N&&R){
		memset(plane,0,sizeof(plane));
		min_x=min_y=INF;
		max_x=max_y=0;
		vector<int> X[2],Y[2];
		REP(i,0,2){
			X[i].resize(N);
			Y[i].resize(N);
		}
		REP(i,0,N){
			REP(j,0,2){
				int x,y;
				cin >> x >> y;
				if(j==0){
					++x;
					++y;
				}
				X[j][i]=x;
				Y[j][i]=y;
				min_x=min(min_x,x);
				min_y=min(min_y,y);
				max_x=max(max_x,x);
				max_y=max(max_y,y);
