const int N = 10010;

class st{
public:
  int height;
  char out;//1 or -1
  bool operator<(const st & a)const{
    if (height != a.height)return height < a.height;
    return out > a.out;
  }
};

short x[N];
vector<st> inx[N];

void makedata(int n,int *x1,int *x2,int *y1,int *y2,
	      int &px,short *xd,vector<st> *IN){
  rep(i,n){
    xd[px++]=x1[i];
    xd[px++]=x2[i];
  }
  sort(xd,xd+px);
  px=unique(xd,xd+px)-xd;
  rep(i,px)IN[i].clear();
  //cout <<"allx ";rep(i,px)cout << x[i]<<" ";cout << endl;
  rep(i,n){
    int j=lower_bound(xd,xd+px,x1[i])-xd;
    while(j < px && xd[j] < x2[i]){
      IN[j].pb((st){y1[i],1});
      IN[j].pb((st){y2[i],-1});
      j++;
    }
  }
}

pair<int,int> solve(int n,vector<st> *in,short *c){
  int area=0;
  int len=0;
  rep(i,n-1){
    if (in[i].size() == 0)continue;
    sort(in[i].begin(),in[i].end());
    int cnt=0,prev=-100;
    int width;
    width=c[i+1]-c[i];
    rep(j,in[i].size()){
      if (cnt > 0){
	area+=(in[i][j].height-prev)*width;
      }
      //if (cnt == 0 && in[i][j].height != prev){
      if (cnt == 0){
	len+=width;
      }
      cnt+=in[i][j].out;
      //if (cnt == 0 && in[i][j].height != prev){
      if (cnt == 0){
	len+=width;
      }
      prev=in[i][j].height;
    }
  }
  return mp(area,len);
}

main(){
