#include <cstdio>
#include <cstring>
#include <algorithm>
#include <vector>

using namespace std;

typedef struct {
    int sx, sy;
    int ex, ey;
} RECT;

int area;
int length;

RECT calc[10000];
RECT before[10000];
RECT grid[10000];
int bsize;

bool comp(const RECT& rLeft, const RECT& rRight){
    return (rLeft.sx < rRight.sx);
}

bool ycomp(const RECT& rLeft, const RECT& rRight){
    return (rLeft.sy < rRight.sy);
}

void mergeArea(int n, int *sq)
{
    int i, j;
    int num;
    int start, end;

    if (n == 0){
        return;
    }

    sort(grid, grid + n, comp);

    num = 0;
    start = grid[0].sx;
    end = grid[0].ex;
    for (i = 1; i < n; i++){
        if (grid[i].sx <= end){
            end = max(end, grid[i].ex);
        }
        else {
            calc[num].sx = start;
            calc[num++].ex = end;
            start = grid[i].sx;
            end = grid[i].ex;
        }
    }
    calc[num].sx = start;
    calc[num++].ex = end;
    for (i = 0; i < num; i++){
        area += calc[i].ex - calc[i].sx;
    }

    *sq = num;
}

void mergeLength(int n)
{
    vector<int> total;
    int i;
    int res;

    for (i = 0; i < n; i++){
        total.push_back(calc[i].sx);
        total.push_back(calc[i].ex);
    }
    for (i = 0; i < bsize; i++){
        total.push_back(before[i].sx);
        total.push_back(before[i].ex);
    }

    sort(total.begin(), total.end());
    res = 0;
    for (i = 0; i < total.size(); i += 2){
        res += total[i + 1] - total[i];
    }
    length += res + 2 * n;
}

int main(void)
{
    int n, query;
    static RECT p[10000];
    int num;
    int i, j;
    int sq;
    int miny, maxy;

    while (1){
        scanf("%d%d", &n, &query);

        if (n + query == 0){
            break;
        }
        area = length = 0;
        miny = 1000000;
        maxy = -100000;
        for (i = 0; i < n; i++){
            scanf("%d%d%d%d", &p[i].sx, &p[i].sy, &p[i].ex, &p[i].ey);
            miny = min(miny, p[i].sy);
            maxy = max(maxy, p[i].ey);
        }
        bsize = 0;
        memset(before, 0, sizeof(before));
        sort(p, p + n, ycomp);
        area = length = 0;
        for (i = miny; i <= maxy; i++){
            memset(grid, 0, sizeof(grid));
            num = sq = 0;
            for (j = 0; j < n; j++){
                if (p[j].sy <= i && i < p[j].ey){
                    grid[num++] = p[j];
                }
                else if (p[j].sy > i){
                    break;
                }
            }
            mergeArea(num, &sq);
            if (query == 2 && (bsize != 0 || num != 0)){
                mergeLength(sq);
            }
            memcpy(before, calc, sizeof(calc));
            bsize = sq;
        }
        printf("%d\n", area);
        if (query == 2){
            printf("%d\n", length);
        }
    }

    return (0);
}

#include <stdio.h>
#include <string.h>

int n;
int min_x1, max_x2, min_y1, max_y2;
int x1[10000], x2[10000], y1[10000], y2[10000];
int tiles[10002][10002];

void pre_process() {
    int i;

    for (i=0;i<n;i++) {
        tiles[y1[i]][x1[i]]++;
        tiles[y1[i]][x2[i]]--;
        tiles[y2[i]][x2[i]]++;
        tiles[y2[i]][x1[i]]--;
    }

    for (i=min_y1;i<max_y2+1;i++) {
        int j;
        for (j=min_x1+1;j<max_x2+1;j++) {
            tiles[i][j] += tiles[i][j-1];
        }
    }


    for (i=min_y1+1;i<max_y2+1;i++) {
        int j;
        for (j=min_x1;j<max_x2+1;j++) {
            tiles[i][j] += tiles[i-1][j];
        }
    }

    return;
}

int surface() {
    int i;
    int sum = 0;

    for (i=min_y1;i<max_y2;i++) {
        int j;
        for (j=min_x1;j<max_x2;j++) {
            if (tiles[i][j] > 0) {
                sum++;
            }
        }
    }

    return sum;
}

int perimeter() {
    int i;
    int sum = 0;

    for (i=min_y1;i<max_y2+2;i++) {
        int j;
        for (j=min_x1;j<max_x2+2;j++) {
            int add = tiles[i][j] + tiles[i-1][j];

            if (add > 0 && (tiles[i][j] == 0 || tiles[i-1][j] == 0)) {
                sum++;
            }

            add = tiles[i][j] + tiles[i][j-1];

            if (add > 0 && (tiles[i][j] == 0 || tiles[i][j-1] == 0)) {
                sum++;
            }
        }
    }

    return sum;
}

int main() {
    while (1) {
        int i;
        int r;

        scanf("%d %d", &n, &r);
        if (n == 0 && r ==0) return 0;

        min_x1 = 10000;
        min_y1 = 10000;
        max_x2 = 0;
        max_y2 = 0;
        for (i=0;i<n;i++) {
            scanf("%d %d %d %d", &x1[i], &y1[i], &x2[i], &y2[i]);
            x1[i]++;
            x2[i]++;
            y1[i]++;
            y2[i]++;
            if (min_x1 > x1[i]) min_x1 = x1[i];
            if (max_x2 < x2[i]) max_x2 = x2[i];
            if (min_y1 > y1[i]) min_y1 = y1[i];
            if (max_y2 < y2[i]) max_y2 = y2[i];
        }

        memset(tiles, 0, 10000 * 10000);
        pre_process();

        printf("%d\n", surface());

        if (r == 2) {
            printf("%d\n", perimeter());
        }        
    }
}

