const int    INF = 1000000000;
const int    MOD = 1000000007;
const double EPS = 1e-8;

int main(){
    int N, R;
    while(cin >> N >> R && R){
        static short tiles[10001][10001] = {};
        for(int y = 0; y <= 10000; y++){
            for(int x = 0; x <= 10000; x++){
                tiles[y][x] = 0;
            }
        }
        int X1 = INF, X2 = -INF, Y1 = INF, Y2 = -INF;
        for(int i = 0; i < N; i++){
            int x1, y1, x2, y2;
            cin >> x1 >> y1 >> x2 >> y2;
            X1 = min(X1, x1); X2 = max(X2, x2);
            Y1 = min(Y1, y1); Y2 = max(Y2, y2);
            tiles[y1][x1]++;
            tiles[y1][x2]--;
            tiles[y2][x1]--;
            tiles[y2][x2]++;
        }
        for(int y = Y1; y <= Y2; y++){
            for(int x = X1 + 1; x <= X2; x++){
                tiles[y][x] += tiles[y][x - 1];
            }
        }
        for(int y = Y1 + 1; y <= Y2; y++){
            for(int x = X1; x <= X2; x++){
                tiles[y][x] += tiles[y - 1][x];
            }
        }
        int ans = 0;
        for(int y = Y1; y <= Y2; y++){
            for(int x = X1; x <= X2; x++){
                ans += (tiles[y][x] > 0 ? 1 : 0);
            }
        }
        cout << ans << endl;
        if(R == 2) cout << -1 << endl;
    }
    return 0;
}

