struct Tag {
    int a;
    int b1, b2;
    bool start;

    Tag() {}
    Tag(int aa, int bb1, int bb2, bool s) : a(aa), b1(bb1), b2(bb2), start(s) {}
};

bool cmp(const Tag &t1, const Tag &t2) {
    if(t1.a != t2.a) return t1.a < t2.a;
    return !t1.start;
}

int main() {
    while(true) {
        int N, R;
        cin >> N >> R;
        if(!N && !R) break;

        vector<Tag> xtag, ytag;
        for(int i = 0; i < N; ++i) {
            int x1, y1, x2, y2;
            cin >> x1 >> y1 >> x2 >> y2;
            xtag.push_back(Tag(x1, y1, y2, true));
            xtag.push_back(Tag(x2, y1, y2, false));
            ytag.push_back(Tag(y1, x1, x2, true));
            ytag.push_back(Tag(y2, x1, x2, false));
        }
        sort(xtag.begin(), xtag.end(), cmp);
        sort(ytag.begin(), ytag.end(), cmp);

        int max_y = ytag.back().a;
        int area = 0;
        int len = 0;
        for(int y = 0; y < max_y; ++y) {
            int depth = 0;
            int startx = 0;
            int prevx = -1;
            for(int i = 0; i < xtag.size(); ++i) {
                if(xtag[i].b1 <= y && y < xtag[i].b2) {
                    if(xtag[i].start) {
                        if(depth == 0) {
                            if(xtag[i].a != prevx) ++len;
                            else --len;
                            startx = xtag[i].a;
                        }
                        ++depth;
                    }
                    else {
                        if(--depth == 0) {
                            area += xtag[i].a-startx;
                            ++len;
                            prevx = xtag[i].a;
                        }
                    }
                }
            }
        }

        cout << area << endl;
        if(R == 1) continue;

        int max_x = xtag.back().a;
        for(int x = 0; x < max_x; ++x) {
            int depth = 0;
            int prevy = -1;
            for(int i = 0; i < ytag.size(); ++i) {
                if(ytag[i].b1 <= x && x < ytag[i].b2) {
                    if(ytag[i].start) {
                        if(depth == 0) {
                            if(prevy != ytag[i].a) ++len;
                            else --len;
                        }
                        ++depth;
                    }
                    else {
                        if(--depth == 0) {
                            ++len;
                            prevy = ytag[i].a;
                        }
                    }
                }
            }
        }
        cout << len << endl;
    }
    return 0;
}

#include <cstdio>
#include <cstring>
#include <algorithm>
#include <vector>

using namespace std;

typedef struct {
    int sx, sy;
    int ex, ey;
} RECT;

int area;
int length;

RECT calc[10000];
RECT before[10000];
RECT grid[10000];
int bsize;

bool comp(const RECT& rLeft, const RECT& rRight){
    return (rLeft.sx < rRight.sx);
}

void mergeArea(int n, int *sq)
{
    int i, j;
    int num;
    int start, end;

    if (n == 0){
        return;
    }

    sort(grid, grid + n, comp);

    num = 0;
    start = grid[0].sx;
    end = grid[0].ex;
    for (i = 1; i < n; i++){
        if (grid[i].sx <= end){
            end = max(end, grid[i].ex);
        }
        else {
            calc[num].sx = start;
            calc[num++].ex = end;
            start = grid[i].sx;
            end = grid[i].ex;
        }
    }
    calc[num].sx = start;
    calc[num++].ex = end;
    for (i = 0; i < num; i++){
        area += calc[i].ex - calc[i].sx;
    }

    *sq = num;
}

void mergeLength(int n)
{
    vector<int> total;
    int i;
    int res;

    for (i = 0; i < n; i++){
        total.push_back(calc[i].sx);
        total.push_back(calc[i].ex);
    }
    for (i = 0; i < bsize; i++){
        total.push_back(before[i].sx);
        total.push_back(before[i].ex);
    }

    sort(total.begin(), total.end());
    res = 0;
    for (i = 0; i < total.size(); i += 2){
        res += total[i + 1] - total[i];
    }
    length += res + 2 * n;
}

int main(void)
{
    int n, query;
    static RECT p[10000];
    int num;
    int i, j;
    int sq;
    int miny, maxy;

    while (1){
        scanf("%d%d", &n, &query);

        if (n + query == 0){
            break;
        }
        area = length = 0;
        miny = 1000000;
        maxy = -100000;
        for (i = 0; i < n; i++){
            scanf("%d%d%d%d", &p[i].sx, &p[i].sy, &p[i].ex, &p[i].ey);
            miny = min(miny, p[i].sy);
            maxy = max(maxy, p[i].ey);
        }
        if (query == 1){
            continue;
        }
        bsize = 0;
        memset(before, 0, sizeof(before));
        area = length = 0;
        for (i = miny; i <= maxy; i++){
            memset(grid, 0, sizeof(grid));
            num = sq = 0;
            for (j = 0; j < n; j++){
                if (p[j].sy <= i && i < p[j].ey){
                    grid[num++] = p[j];
                }
            }
            mergeArea(num, &sq);
            if (query == 2 && (bsize != 0 || num != 0)){
                mergeLength(sq);
            }
            memcpy(before, calc, sizeof(calc));
            bsize = sq;
        }
        printf("%d\n", area);
        if (query == 1){
            continue;
        }
        printf("%d\n", length);
    }

    return (0);
}

#include<iostream>
#include<cstring>
#include<algorithm>
using namespace std;

int map[10003][10003];

int main(void){
	while(1){
		int n,r,ans=0,ans2=0,minx=0,miny=0,maxx=10001,maxy=10001;
		scanf("%d%d",&n,&r);
		if(n==0 && r==0)break;
		memset(map,0,sizeof(map));
		for(int i=0;i<n;i++){
			int x1,y1,x2,y2;
			scanf("%d%d%d%d",&x1,&y1,&x2,&y2);
			minx=max(x1+1,minx);
			miny=max(y1+1,miny);
			maxx=min(x2+1,maxx);
			maxy=min(y2+1,maxy);
			map[x1+1][y1+1]-=1;
			map[x2+1][y1+1]+=1;
			map[x1+1][y2+1]+=1;
			map[x2+1][y2+1]-=1;
		}
		for(int i=miny;i<=maxy;i++){
			for(int j=minx;j<=maxy;j++){
				map[j][i]=map[j][i]+map[j-1][i];
			}
		}
		for(int i=minx;i<=maxx;i++){
			for(int j=maxy;j>=miny;j--){
				map[i][j]=map[i][j]+map[i][j+1];
				if(map[i][j]>=1){
					ans++;
				}
			}
		}
		printf("%d\n",ans);
		if(r==2){
			for(int i=minx;i<=maxx;i++){
				for(int j=miny;j<=maxy;j++){
					if(map[i][j]>=1){
						if(map[i-1][j]<=0)ans2++;
						if(map[i+1][j]<=0)ans2++;
						if(map[i][j-1]<=0)ans2++;
						if(map[i][j+1]<=0)ans2++;
					}
				}
			}
			printf("%d\n",ans2);
		}
	}
    return 0;
}

#include <cstdio>
