using namespace std;

#define ALL(c) c.begin(),c.end()
#define RALL(c) c.rbegin(),c.rend()
#define SORT(x) sort((x).begin(),(x).end())
#define REP(i,x,y) for(int i=(x);i<(y);++i)
#define MP(a,b) make_pair((a),(b))
#define F_ first
#define S_ second

typedef long long int lli;
typedef pair<short,short> P;

const int INF=1<<30;
//const long long int INF_=1<<58;
const int dx[]={1,0,-1,0},dy[]={0,1,0,-1};

int min_x,min_y,max_x,max_y;

inline void Imos(map<P,short> &plane){
	REP(y,0,max_y+4-min_y){
		REP(x,1,max_x+4-min_x){
			plane[MP(x,y)]+=plane[MP(x-1,y)];
		}
	}
	REP(x,0,max_x+4-min_x){
		for(int y=max_y+4-min_y; y>=0; --y){
			plane[MP(x,y)]+=plane[MP(x,y+1)];
		}
	}
}

inline int CalcSize(map<P,short> &plane){
	int result=0;
	REP(x,0,max_x+4-min_x){
		REP(y,0,max_y+4-min_y){
			if(plane[MP(x,y)]>0) ++result;
		}
	}
	return result;
}

inline int BFS(map<P,short> &plane,map<P,bool> &has_done,int x,int y,int W,int H){
	int result=0;
	queue<P> que;
	que.push(MP(x,y));
	has_done[MP(x,y)]=true;
	while(!que.empty()){
		P p=que.front();
		que.pop();
		REP(i,0,4){
			int nx=p.F_+dx[i],ny=p.S_+dy[i];	
			if(0<=nx&&nx<W&&0<=ny&&ny<H&&!has_done[MP(nx,ny)]){
				if(plane[MP(nx,ny)]>0){
					que.push(MP(nx,ny));
					has_done[MP(nx,ny)]=true;
				}else ++result;
			}
		}
		//cout << result << ' ' << p.F_ << ' ' << p.S_ << endl;
	}
	return result;
}

inline int CalcLength(map<P,short> &plane,vector<int> &X,vector<int> &Y){
	map<P,bool> has_done;
	int result=0;
	REP(i,0,X.size()){
		if(!has_done[MP(X[i],Y[i])]&&plane[MP(X[i],Y[i])]>0){
			result+=BFS(plane,has_done,X[i],Y[i],max_x+4-min_x,max_y+4-min_y);
		}
	}
	return result;
}

int main(){
	std::ios::sync_with_stdio(false);
	std::cin.tie(0);
	int N,R;
	while(cin >> N >> R&&N&&R){
		min_x=min_y=INF;
		max_x=max_y=0;
		vector<int> X[2],Y[2];
		REP(i,0,N){
			REP(j,0,2){
				int x,y;
				cin >> x >> y;
				if(j==0){
					++x;
					++y;
				}
				X[j].push_back(x);
				Y[j].push_back(y);
				min_x=min(min_x,x);
				min_y=min(min_y,y);
				max_x=max(max_x,x);
				max_y=max(max_y,y);
			}
		}
		//vector<vector<short> > plane(max_x+4-min_x,vector<short>(max_y+4-min_y));
		map<P,short> plane;
		REP(i,0,N){
			plane[MP(X[0][i]+1-min_x,Y[1][i]+1-min_y)]+=1;
			plane[MP(X[1][i]+2-min_x,Y[0][i]-min_y)]+=1;
			plane[MP(X[0][i]+1-min_x,Y[0][i]-min_y)]+=-1;
			plane[MP(X[1][i]+2-min_x,Y[1][i]+1-min_y)]+=-1;
		}
		Imos(plane);
		/*
		for(int y=plane[0].size()-1; y>=0; --y){
			REP(x,0,plane.size()){
				cout << plane[x][y] << ' ';
			}
			cout << endl;
		}
		*/
		cout << CalcSize(plane) << endl;
		if(R==2) cout << CalcLength(plane,X[0],Y[0]) << endl;
	}
	return 0;
}

