int main(){
  int n,r;
  int x1[10010],y1[10010],x2[10010],y2[10010];
  int imos[10010];

  while(cin >> n >> r,n){
    for(int i=0;i<n;i++)cin >> x1[i] >> y1[i] >> x2[i] >> y2[i];

    int area = 0;
    for(int i=0;i<=10000;i++){
      INIT(imos);
      for(int j=0;j<n;j++){
	if(x1[j]<=i && i<x2[j]){
	  imos[y1[j]]++; imos[y2[j]]--;
	}
      }
      for(int i=0;i<10000;i++)imos[i+1] += imos[i];
      for(int i=0;i<=10000;i++){
	if(imos[i])area++;
      }
    }

    cout << area << endl;

    if(r==2){
      int len = 0;
      for(int t=0;t<2;t++){
	for(int i=0;i<=10000;i++){
	  INIT(imos);
	  for(int j=0;j<n;j++){
	    if(x1[j]<=i && i<x2[j]){
	      imos[y1[j]]++; imos[y2[j]]--;
	    }
	  }
	  for(int i=0;i<10000;i++)imos[i+1] += imos[i];
	  for(int i=0;i<=10000;i++){
	    if(imos[i]){
	      if(i==0 || imos[i-1]==0)len++;
	      if(i==10000 || imos[i+1]==0)len++;
	    }
	  }
	}
