const int MAX = 10000;

struct Sheet {int left, bottom, right, top;};
bool operator<(const Sheet& lhs, const Sheet& rhs) {
    if(lhs.bottom != rhs.bottom) return lhs.bottom < rhs.bottom;
    return lhs.top < rhs.top;
}

struct Range {
    int bottom, top;
    Range(const int bottom, const int top) :bottom(bottom), top(top) {}
};

int main() {
    int n, r;
    while(cin >> n >> r, n | r) {
        vector<vector<Range>> list(MAX + 1);
        vector<Sheet> sheet(n);
        for(auto& s: sheet) cin >> s.left >> s.bottom >> s.right >> s.top;

        sort(sheet.begin(), sheet.end());
        for(auto s: sheet) for(int x = s.left; x < s.right; ++x) {
            for(int i = 0; i < list[x].size(); ++i) {
                if(list[x][i].top < s.bottom) continue;
                list[x][i].top = max(list[x][i].top, s.top);
                while(i + 1 < list[x].size() && list[x][i].top >= list[x][i + 1].bottom) {
                    list[x][i].top = max(list[x][i].top, list[x][i + 1].top);
                    list[x].erase(list[x].begin() + i + 1);
                }
                break;
            }
            if(list[x].empty() || list[x].back().top < s.bottom) list[x].emplace_back(s.bottom, s.top);
        }

        int area = 0, circumference = 0;
        int length, touch_left = 0, touch_right;
        for(int x = 0; x < list.size(); ++x) {
            if(list[x].empty()) continue;
            length = touch_right = 0;
            for(const auto& l: list[x]) {
                length += l.top - l.bottom;
                for(const auto& l2: list[x + 1]) {
                    if(l.top < l2.bottom || l2.top < l.bottom) continue;
                    touch_right += min(l.top, l2.top) - max(l.bottom, l2.bottom);
                }
            }
            area += length;
            circumference += list[x].size() * 2;
            circumference += 2 * length - touch_left - touch_right;
            touch_left = touch_right;
        }

        cout << area << endl;
        if(r == 2) cout << circumference << endl;
    }
}

