#include<iostream>
#include<vector>
#include<map>
#include<algorithm>
using namespace std;

class Compression {
public:
    Compression(const std::vector<int>& list) {
        value_ = list;
        std::sort(value_.begin(), value_.end());
        value_.erase(std::unique(value_.begin(), value_.end()), value_.end());
        for(int i = 0; i < value_.size(); ++i) index_[value_[i]] = i;
    }
    int size() const {return value_.size();}
    int index(const int v) const {return index_.at(v);}
    int operator[](const int i) const {return value_.at(i);}
private:
    std::map<int, int> index_;
    std::vector<int> value_;
};

struct Square {int left, top, right, bottom;};

int main() {
    int n, r;
    while(cin >> n >> r, n | r) {
        vector<int> tx = {0}, ty = {0};
        vector<Square> sheet(n);
        for(auto& s: sheet) {
            cin >> s.left >> s.top >> s.right >> s.bottom;
            tx.push_back(++s.left);
            tx.push_back(++s.right);
            ty.push_back(++s.top);
            ty.push_back(++s.bottom);
        }
        Compression x(tx), y(ty);

        vector<vector<int>> dp(y.size(), vector<int>(x.size(), 0));
        for(const auto& s: sheet) {
            ++dp[y.index(s.top)][x.index(s.left)];
            --dp[y.index(s.top)][x.index(s.right)];
            --dp[y.index(s.bottom)][x.index(s.left)];
            ++dp[y.index(s.bottom)][x.index(s.right)];
        }

        for(int i = 0; i < y.size(); ++i) for(int j = 1; j < x.size(); ++j) dp[i][j] += dp[i][j - 1];
        for(int j = 0; j < x.size(); ++j) for(int i = 1; i < y.size(); ++i) dp[i][j] += dp[i - 1][j];

        int area = 0, circumference = 0;
        for(int i = 1; i < y.size() - 1; ++i) for(int j = 1; j < x.size() - 1; ++j) {
            if(!dp[i][j]) continue;
            area += (y[i + 1] - y[i]) * (x[j + 1] - x[j]);
            if(!dp[i - 1][j]) circumference += (x[j + 1] - x[j]);
            if(!dp[i + 1][j]) circumference += (x[j + 1] - x[j]);
            if(!dp[i][j - 1]) circumference += (y[i + 1] - y[i]);
            if(!dp[i][j + 1]) circumference += (y[i + 1] - y[i]);
        }
        cout << area << endl;
        if(r) cout << circumference << endl;
    }
}

