using namespace std;

int n,r;
int x1[10001],x2[10001],y1[10001],y2[10001];
int fie[2000][2000];
vector<int> vx;
vector<int> vy;

void zatu(){
	for(int i=0;i<n;i++){
		for(int j=-1;j<=1;j++){
			vx.push_back(x1[i]+j);
			vx.push_back(x2[i]+j);
			vy.push_back(y1[i]+j);
			vy.push_back(y2[i]+j);
		}
	}
	vx.push_back(0);
	vx.push_back(10000);
	vy.push_back(0);
	vy.push_back(10000);
	sort(vx.begin(),vx.end());
	sort(vy.begin(),vy.end());
	vx.erase(unique(vx.begin(),vx.end()),vx.end());
	vy.erase(unique(vy.begin(),vy.end()),vy.end());
	for(int i=0;i<n;i++){
		x1[i]=find(vx.begin(),vx.end(),x1[i])-vx.begin();
		x2[i]=find(vx.begin(),vx.end(),x2[i])-vx.begin();
		y1[i]=find(vy.begin(),vy.end(),y1[i])-vy.begin();
		y2[i]=find(vy.begin(),vy.end(),y2[i])-vy.begin();
	}
}

int main(void){
	while(1){
		scanf("%d%d",&n,&r);
		if(n==0 && r==0)break;
		vx.clear();
		vy.clear();
		memset(fie,0,sizeof(fie));
		for(int i=0;i<n;i++)scanf("%d%d%d%d",&x1[i],&y1[i],&x2[i],&y2[i]);
		zatu();
		for(int i=0;i<n;i++){
			fie[x1[i]][y1[i]]++;
			fie[x1[i]][y2[i]]--;
			fie[x2[i]][y1[i]]--;
			fie[x2[i]][y2[i]]++;
		}
		for(int i=0;i<=vy.size();i++){
			for(int j=1;j<=vx.size();j++){
				fie[j][i]+=fie[j-1][i];
			}
		}
		for(int i=0;i<=vx.size();i++){
			for(int j=1;j<=vy.size();j++){
				fie[j][i]+=fie[j][i-1];
			}
		}
		long long res=0;
		for(int i=0;i<vx.size();i++){
			for(int j=0;j<vy.size();j++){
				if(fie[j][i]>0)res+=(long long)(vx[i+1]-vx[i])*(vy[j+1]-vy[j]);
			}
		}

		cout << res << endl;
		if(r==2)cout << res << endl;
	}
    return 0;
}

