#define pb         push_back

const int N = 10010;

class st{
public:
  short height;
  char out;//1 or -1
  bool operator<(const st & a)const{
    if (height != a.height)return height < a.height;
    return out > a.out;
  }
};

int x[N];
vector<st> inx[N];

void makedata(int n,short *x1,short *x2,short *y1,short *y2,
	      int &px,int *xd,vector<st> *IN){
  rep(i,n){
    xd[px++]=x1[i];
    xd[px++]=x2[i];
  }
  sort(xd,xd+px);
  px=unique(xd,xd+px)-xd;
  rep(i,px)IN[i].clear();
  //cout <<"allx ";rep(i,px)cout << x[i]<<" ";cout << endl;
  rep(i,n){
    int j=lower_bound(xd,xd+px,x1[i])-xd;
    while(j < px && xd[j] < x2[i]){
      IN[j].pb((st){y1[i],1});
      IN[j].pb((st){y2[i],-1});
      j++;
    }
  }
}

pair<int,int> solve(int n,vector<st> *in,int *c){
  int area=0;
  int len=0;
  rep(i,n-1){
    if (in[i].size() == 0)continue;
    sort(in[i].begin(),in[i].end());
    int cnt=0,prev=-100;
    int width;
    width=c[i+1]-c[i];
    rep(j,in[i].size()){
      if (cnt > 0){
	area+=(in[i][j].height-prev)*width;
      }
      //if (cnt == 0 && in[i][j].height != prev){
      if (cnt == 0){
	len+=width;
      }
      cnt+=in[i][j].out;
      //if (cnt == 0 && in[i][j].height != prev){
      if (cnt == 0){
	len+=width;
      }
      prev=in[i][j].height;
    }
  }
  return mp(area,len);
}

main(){
  int n,q;
  static short x1[N],y1[N],x2[N],y2[N];
  while(cin>>n>>q && n){
    rep(i,n){
      cin>>x1[i]>>y1[i]>>x2[i]>>y2[i];
    }
    int px=0,py=0;
    makedata(n,x1,x2,y1,y2,px,x,inx);
    pair<int,int> ansx=solve(px,inx,x);

    cout << ansx.first << endl;
    if (q == 2){
      px=0;
      makedata(n,y1,y2,x1,x2,px,x,inx);
      pair<int,int> ansy= solve(px,inx,x);      
      cout << ansx.second+ansy.second << endl;
    }
  }
  return false;
}

