#include <stdio.h>
#include <stdlib.h>

typedef struct range
{
  int a;
  int z;
  struct range *next;
} range;


range *rangenew(int r1, int r2, range *nx)
{
  range *rng;
  rng = (range *)malloc(sizeof(range));
  rng->a = r1;
  rng->z = r2;
  rng->next = nx;
  return rng;
}

range *insert(range *line, int r1, int r2)
{
  range *rng;
  int z2;
  if(line == NULL){
    return rangenew(r1, r2, NULL);
  }
  if(line->z < r1){
    line->next = insert(line->next, r1, r2);
    return line;
  }
  if(r2 < line->a){
    return rangenew(r1, r2, line);
  }
  line->a = (r1 < line->a ? r1 : line->a);
  z2 = line->z;
  rng = line->next;
  while(rng != NULL && rng->a <= r2) {
    z2 = rng->z;
    free(rng);
    rng = rng->next;
  }
  line->z = (r2 > z2 ? r2 : z2);
  line->next = rng;
  return line;
}

void linefree(range *line)
{
  if(line){
    free(line);
    linefree(line->next);
  }
}

