#include <cstdio>
#include <cstring>
#include <algorithm>
#include <vector>

using namespace std;

typedef struct {
    int sx, sy;
    int ex, ey;
} RECT;

int area;
int length;

RECT calc[10000];
RECT before[10000];
RECT grid[10000];
int bsize;

bool comp(const RECT& rLeft, const RECT& rRight){
    return (rLeft.sx < rRight.sx);
}

void mergeArea(int n, int *sq)
{
    int i, j;
    int num;
    int start, end;

    if (n == 0){
        return;
    }

    sort(grid, grid + n, comp);

    num = 0;
    start = grid[0].sx;
    end = grid[0].ex;
    for (i = 1; i < n; i++){
        if (grid[i].sx <= end){
            end = max(end, grid[i].ex);
        }
        else {
            calc[num].sx = start;
            calc[num++].ex = end;
            start = grid[i].sx;
            end = grid[i].ex;
        }
    }
    calc[num].sx = start;
    calc[num++].ex = end;
    for (i = 0; i < num; i++){
        area += calc[i].ex - calc[i].sx;
    }

    *sq = num;
}

void mergeLength(int n)
{
    vector<int> total;
    int i;
    int res;

    for (i = 0; i < n; i++){
        total.push_back(calc[i].sx);
        total.push_back(calc[i].ex);
    }
    for (i = 0; i < bsize; i++){
        total.push_back(before[i].sx);
        total.push_back(before[i].ex);
    }

    sort(total.begin(), total.end());
    res = 0;
    for (i = 0; i < total.size(); i += 2){
        res += total[i + 1] - total[i];
    }
    length += res + 2 * n;
}

int main(void)
{
    int n, query;
    static RECT p[10000];
    int num;
    int i, j;
    int sq;
    int miny, maxy;

    while (1){
        scanf("%d%d", &n, &query);

        if (n + query == 0){
            break;
        }
        area = length = 0;
        miny = 1000000;
        maxy = -100000;
        for (i = 0; i < n; i++){
            scanf("%d%d%d%d", &p[i].sx, &p[i].sy, &p[i].ex, &p[i].ey);
            miny = min(miny, p[i].sy);
            maxy = max(maxy, p[i].ey);
        }
        if (query == 1){
            continue;
        }
        bsize = 0;
        memset(before, 0, sizeof(before));
        area = length = 0;
        for (i = miny; i <= maxy; i++){
            memset(grid, 0, sizeof(grid));
            num = sq = 0;
            for (j = 0; j < n; j++){
                if (p[j].sy <= i && i < p[j].ey){
                    grid[num++] = p[j];
                }
            }
            mergeArea(num, &sq);
            if (query == 2 && (bsize != 0 || num != 0)){
                mergeLength(sq);
            }
            memcpy(before, calc, sizeof(calc));
            bsize = sq;
        }
        printf("%d\n", area);
        printf("%d\n%d\n", area, length);
    }

    return (0);
}

