        maxx = max(maxx,x_2);
        maxy = max(maxy,y_2);
    }

    for(int i=miny;i<=maxy;i++){
        for(int j=minx+1;j<=maxx;j++){
            mm[j][i]+=mm[j-1][i];
        }
    }

    for(int i=minx;i<=maxx;i++){
        for(int j=miny+1;j<=maxy;j++){
            mm[i][j]+=mm[i][j-1];
