short  isexist[N]={0};
short testcase=0;
int x[N];
st *inx[N];

int req[N];

void makedata(int n,int *x1,int *x2,int *y1,int *y2,
	      int &px,int *xd,st *IN[N]){
  testcase++;
  rep(i,n){
    isexist[x1[i]]=testcase;
    isexist[x2[i]]=testcase;
  }
  rep(i,N)if (isexist[i]==testcase)x[px]=i,free(IN[px++]);
  //cout <<"allx ";rep(i,px)cout << x[i]<<" ";cout << endl;

  rep(i,px)req[i]=0;
  rep(i,n){
    int j=lower_bound(xd,xd+px,x1[i])-xd;
    while(j < px && xd[j] < x2[i]){
      //IN[j].pb((st){y1[i],y2[i]});
      req[j]++;
      j++;
    }
  }

  rep(i,px){
    IN[i]=(st*)malloc(sizeof(st)*req[i]);
    req[i]=0;
  }

  rep(i,n){
    int j=lower_bound(xd,xd+px,x1[i])-xd;
    while(j < px && xd[j] < x2[i]){
      //IN[j].pb((st){y1[i],y2[i]});
      IN[j][req[j]++]=(st){y1[i],y2[i]};
      j++;
    }
  }
}

pair<int,int> solve(int n,st *in[N],int *c){
  int area=0;
  int len=0;
  rep(i,n-1){
    if (req[i] == 0)continue;
    sort(in[i],in[i]+req[i]);
    short l=-1,h=-1;
    short width;
    width=c[i+1]-c[i];
    rep(j,req[i]){
      short &nowl=in[i][j].beg,&nowh=in[i][j].end;
      //cout << nowl <<" "<< nowh <<" " << l<<" " << h << endl;
      if (nowl > h){
	len+=2*width;
	//cout << c[i]<<" " << c[i]<<" " << nowh<<" " << nowl << endl;
	area+=(nowh-nowl)*width;
      }else if (nowl == h){
	//cout << c[i]<<" " << c[i]<<" " << nowh<<" " << nowl << endl;
	area+=(nowh-nowl)*width;
      }else if (nowh > h){
	//cout << c[i]<<" " << c[i]<<" " << nowh<<" " << h << endl;
	area+=(nowh-h)*width;
      }
      l=nowl;
      h=max(h,nowh);
    }
  }
  return mp(area,len);
}

main(){
  int n,q;
  static int x1[N],y1[N],x2[N],y2[N];
  while(cin>>n>>q && n){
    rep(i,n){
      cin>>x1[i]>>y1[i]>>x2[i]>>y2[i];
    }
    int px=0,py=0;
    makedata(n,x1,x2,y1,y2,px,x,inx);
