int CalcSize(vector<vector<P> > &range,vector<Rect> &rects){
	int result=0;
	int index=0;
	REP(x,0,10010){
		while(index<rects.size()&&rects[index].x1<=x){
			REP(i,rects[index].x1,rects[index].x2) range[i].push_back(MP(rects[index].y1,rects[index].y2));
			++index;
		}
		sort(range[x].begin(),range[x].end());
		int y=0;
		REP(i,0,range[x].size()){
			if(range[x][i].S_>y) result+=range[x][i].S_-max(y,range[x][i].F_);
			y=max(y,range[x][i].S_);
		}
		//range[x].clear();
		vector<P>().swap(range[x]);
	}
	return result;
}

int Count(vector<vector<P> > &range,vector<vector<bool> > &flag,int x){
	int index=0,y=0,result=0;
	while(index<range[x].size()){
		for(y=max(y,range[x][index].F_); y<range[x][index].S_; ++y){
			if(!flag[(x-1)%3][y]) ++result;
			if(!flag[(x+1)%3][y]) ++result;
			if(!flag[x%3][y-1]) ++result;
			if(!flag[x%3][y+1]) ++result;
		}
		++index;
	}
	return result;
}

int CalcLength(vector<vector<P> > &range,vector<Rect> &rects){
	int result=0,index=0;
	vector<vector<bool> > flag(3,vector<bool>(10010));
	REP(x,1,10009){
		while(index<rects.size()&&rects[index].x1<=x+1){
			REP(i,rects[index].x1,rects[index].x2) range[i].push_back(MP(rects[index].y1,rects[index].y2));
			++index;
		}
		sort(range[x+1].begin(),range[x+1].end());
		if(x-2>=0) fill(flag[(x-2)%3].begin(),flag[(x-2)%3].end(),false);
		int y=0;
		REP(i,0,range[x+1].size()){
			if(range[x+1][i].S_>y) fill_n(flag[(x+1)%3].begin()+max(y,range[x+1][i].F_),range[x+1][i].S_-max(y,range[x+1][i].F_),true);
			y=max(range[x+1][i].S_,y);
		}
		result+=Count(range,flag,x);
		if(x-2>=0){
			//range[x-2].clear();
			vector<P>().swap(range[x-2]);
		}
	}
	return result;
}

