#include <iostream>
#include <vector>

using namespace std;

struct node {
	double x, r;
};

vector< vector<node> > emp, vx, vy;

node make_node(double x, double r) {
	node v;
	v.x = x, v.r = r;
	return v;
}

double abs(double n) {
	return n < 0 ? -n : n;
}

void make_area(vector< vector<node> > &v, int n, int a, int b) {
	double x = (a + b) / 2.0, r = b - x;
	for (int i = 0; i < v[n].size(); ++i) {
		node nd = v[n][i];
		if (abs(x - nd.x) <= abs(r + nd.r)) {
			v[n][i].x = (min(x - r, nd.x - nd.r) + max(x + r, nd.x + nd.r)) / 2.0;
			v[n][i].r = max(x + r, nd.x + nd.r) - v[n][i].x;
			return;
		}
	}
	v[n].push_back( make_node(x, r) );
}

int main() {
	int n, r;

	emp.resize(10002);

	while (cin >> n >> r, n || r) {
		vx = vy = emp;
		for (int i = 0; i < n; ++i) {
			int x1, x2, y1, y2;
			cin >> x1 >> y1 >> x2 >> y2;

			for (int y = y1; y < y2; ++y)
				make_area(vx, y, x1, x2);

			for (int x = x1; x < x2; ++x)
				make_area(vy, x, y1, y2);
		}

		int S = 0, l = 0;
		for (int i = 0; i < 10002; ++i) {
			for (int x = 0; x < vx[i].size(); ++x)
				S += (int)(2 * vx[i][x].r);

			l += 2 * (vx[i].size() + vy[i].size());
		}

		cout << S << endl;
		if (r == 2) cout << l << endl;
	}
}

