void makedata(int n,short *x1,short *x2,short *y1,short *y2,
	      int &px,int &py){
  vector<short> xd,yd;
  rep(i,n){
    xd.pb(x1[i]);
    xd.pb(x2[i]);
    yd.pb(y1[i]);
    yd.pb(y2[i]);
  }
  sort(ALL(xd));
  sort(ALL(yd));
  xd.erase(unique(ALL(xd)),xd.end());
  yd.erase(unique(ALL(yd)),yd.end());
  rep(i,xd.size())x[px++]=xd[i],inx[i].clear();
  rep(i,yd.size())y[py++]=yd[i],iny[i].clear();

  //cout <<"allx ";rep(i,px)cout << x[i]<<" ";cout << endl;
  rep(i,n){
    int j=lower_bound(xd.begin(),xd.end(),x1[i])-xd.begin();
    while(j < px && x[j] < x2[i]){
      inx[j].pb((st){y1[i],1});
      inx[j].pb((st){y2[i],-1});
      j++;
    }
    j = lower_bound(ALL(yd),y1[i])-yd.begin();
    while(j < py && y[j] < y2[i]){
      iny[j].pb((st){x1[i],1});
      iny[j].pb((st){x2[i],-1});
      j++;
    }
  }
}

pair<int,int> solve(int n,vector<st> *in,short *c){
  int area=0;
  int len=0;
  rep(i,n-1){
    if (in[i].size() == 0)continue;
    sort(in[i].begin(),in[i].end());
    int cnt=0,prev=-100;
    int width;
    width=c[i+1]-c[i];
    rep(j,in[i].size()){
      if (cnt > 0){
	area+=(in[i][j].height-prev)*width;
      }
      //if (cnt == 0 && in[i][j].height != prev){
      if (cnt == 0){
	len+=width;
      }
      cnt+=in[i][j].out;
      //if (cnt == 0 && in[i][j].height != prev){
      if (cnt == 0){
	len+=width;
      }
      prev=in[i][j].height;
    }
  }
  return mp(area,len);
}

main(){
  int n,q;
  static short x1[N],y1[N],x2[N],y2[N];
  while(cin>>n>>q && n){
    rep(i,n){
      cin>>x1[i]>>y1[i]>>x2[i]>>y2[i];
    }
    int px=0,py=0;
    makedata(n,x1,x2,y1,y2,px,py);
    pair<int,int> ansx=solve(px,inx,x);
    cout << ansx.first << endl;
    if (q == 2){
      pair<int,int> ansy= solve(py,iny,y);
      cout << ansx.second+ansy.second << endl;
    }
  }
  return false;
}

