using namespace std;

struct Tag {
    int a;
    int b1, b2;
    bool start;

    Tag() : a(0), b1(0), b2(0), start(false) {}
    Tag(int aa, int bb1, int bb2, bool s) : a(aa), b1(bb1), b2(bb2), start(s) {}
};

bool cmp(const Tag &t1, const Tag &t2) {
    if(t1.a != t2.a) return t1.a < t2.a;
    return !t1.start;
}

int main() {
    while(true) {
        int N, R;
        cin >> N >> R;
        if(!N && !R) break;

        vector<Tag> xtag, ytag;
        for(int i = 0; i < N; ++i) {
            int x1, y1, x2, y2;
            cin >> x1 >> y1 >> x2 >> y2;
            xtag.push_back(Tag(x1, y1, y2, true));
            xtag.push_back(Tag(x2, y1, y2, false));
            ytag.push_back(Tag(y1, x1, x2, true));
            ytag.push_back(Tag(y2, x1, x2, false));
        }
        sort(xtag.begin(), xtag.end(), cmp);
        sort(ytag.begin(), ytag.end(), cmp);

        int max_y = ytag[ytag.size()-1].a;
        int area = 0;
        int len = 0;
        for(int y = 0; y < max_y; ++y) {
            int depth = 0;
            int startx = 0;
            int prevx = -1;
            for(int i = 0; i < xtag.size(); ++i) {
                if(xtag[i].b1 <= y && y < xtag[i].b2) {
                    if(xtag[i].start) {
                        if(depth == 0) {
                            if(xtag[i].a != prevx) ++len;
                            else --len;
                            startx = xtag[i].a;
                        }
                        ++depth;
                    }
                    else {
                        if(--depth == 0) {
                            area += xtag[i].a-startx;
                            prevx = xtag[i].a;
                            ++len;
                        }
                    }
                }
            }
        }

        cout << area << endl;
        if(R == 1) continue;

        int max_x = xtag[xtag.size()-1].a;
        for(int x = 0; x < max_x; ++x) {
            int depth = 0;
            int prevy = -1;
            for(int i = 0; i < ytag.size(); ++i) {
                if(ytag[i].b1 <= x && x < ytag[i].b2) {
                    if(ytag[i].start) {
                        if(depth == 0) {
                            if(prevy != ytag[i].a) ++len;
                            else --len;
                        }
                        ++depth;
                    }
                    else {
                        if(--depth == 0) {
                            ++len;
                            prevy = ytag[i].a;
                        }
                    }
                }
            }
        }
        cout << len << endl;
    }
    return 0;
}

#include<cstdio>
#include<vector>
#include<queue>
#include<algorithm>
using namespace std;

int cnt[10005][10005];

int compress(vector<int> &v){
  vector<int> x;
  for(int i=0;i<v.size();i++){
    x.push_back(v[i]);
  }
  v.erase(v.begin(),v.end());
  sort(x.begin(),x.end());
  x.erase(unique(x.begin(),x.end()),x.end());
  for(int i=0;i<x.size();i++){
    v.push_back(x[i]);
  }
  return v.size();
}

int solve(){
  int n,r,x1[10000],x2[10000],y1[10000],y2[10000];
  vector<int> x,y;
  scanf("%d %d",&n,&r);
  if(n==0&&r==0) return 1;
  for(int i=0;i<n;i++){
    scanf("%d %d %d %d",x1+i,y1+i,x2+i,y2+i);
    x.push_back(x1[i]);
    x.push_back(x2[i]);
    y.push_back(y1[i]);
    y.push_back(y2[i]);
  }
  int size_x = compress(x);
  int size_y = compress(y);
  for(int i=0;i<size_x;i++) for(int j=0;j<size_y;j++) cnt[i][j] = 0;
  for(int i=0;i<n;i++){
    int index_x1,index_x2,index_y1,index_y2;
    index_x1 = lower_bound(x.begin(),x.end(),x1[i])-x.begin();
    index_x2 = lower_bound(x.begin(),x.end(),x2[i])-x.begin();
    index_y1 = lower_bound(y.begin(),y.end(),y1[i])-y.begin();
    index_y2 = lower_bound(y.begin(),y.end(),y2[i])-y.begin();
    cnt[index_x1][index_y1]++;
    cnt[index_x2][index_y1]--;
    cnt[index_x1][index_y2]--;
    cnt[index_x2][index_y2]++;
  }
  for(int i=0;i<size_x;i++){
    for(int j=0;j<size_y;j++){
      cnt[i][j+1] += cnt[i][j];
    }
  }
  for(int i=0;i<size_y;i++){
    for(int j=0;j<size_x;j++){
      cnt[j+1][i] += cnt[j][i];
    }
  }
  int s = 0;
  int l = 0;
  for(int i=0;i<size_x-1;i++){
    for(int j=0;j<size_y-1;j++){
      if(cnt[i][j]>0){
        s += (x[i+1]-x[i])*(y[j+1]-y[j]);
        int di[] = {0,0,1,-1};
        int dj[] = {1,-1,0,0};
        for(int k=0;k<4;k++){
          int ni = i+di[k];
          int nj = j+dj[k];
          if(ni>=0&&nj>=0&&ni<size_x-1&&nj<size_y-1&&cnt[ni][nj]>0) continue;
          if(di[k]==0) l += x[i+1]-x[i];
          else if(dj[k]==0) l += y[j+1]-y[j];
        }
      }
    }
  }
  if(r==1) printf("%d\n",s);
  else if(r==2) printf("%d\n%d\n",s,l);
  return 0;
}

int main(){
  while(true){
    if(solve()) return 0;
  }
}

