#define MAX 10003

int main(){
	int n, r;
	while(scanf("%d%d", &n, &r), n){
		vector<vector<pair<int,int> > > ev(MAX);
		int x1, x2, y1, y2;

		for(int i = 0; i < n; ++i){
			scanf("%d%d%d%d", &x1, &y1, &x2, &y2);
			ev[x1+1].push_back(make_pair(y1+1, 1));
			ev[x1+1].push_back(make_pair(y2+1, -1));
			ev[x2+1].push_back(make_pair(y1+1, -1));
			ev[x2+1].push_back(make_pair(y2+1, 1));
		}

		int area = 0, len = 0;

		vector<int> prev(MAX), now = prev, next = prev;
		for(int i = 0; i + 1 < MAX; ++i){
			fill(next.begin(), next.end(), 0);
			for(int j = 0; j < ev[i+1].size(); ++j){
				next[ev[i+1][j].first] += ev[i+1][j].second;
			}
			for(int j = 1; j < MAX; ++j){
				next[j] += next[j-1];
			}
			for(int j = 0; j < MAX; ++j){
				next[j] += now[j];
			}

			for(int j = 1; j + 1 < MAX; ++j){
				if(now[j] == 0) continue;
				++area;

				if(prev[j] == 0) ++len;
				if(next[j] == 0) ++len;
				if(now[j-1] == 0) ++len;
				if(now[j+1] == 0) ++len;
			}

			prev.swap(now);
			now.swap(next);
		}

		printf("%d\n", area);
		if(r == 2) printf("%d\n", len);
	}
}

