    int n, r;
    cin >> n >> r;
    if (!(n || r)) return 0;
    memset(sheet, 0, sizeof sheet);
    int mx = 0, my = 0, area = 0;
    for (int i = 0; i < n; i++) {
      int sx, sy, ex, ey;
      cin >> sx >> sy >> ex >> ey;
      mx = max(mx, ex);
      my = max(my, ey);
      for (int x = sx; x < ex; x++)
        for (int y = sy; y < ey; y++)
          if (!sheet[x][y]) {
            sheet[x][y] = true;
            area++;
          }
    }
    cout << area << endl;
    if (r == 1) continue;
    int circumference = 4 * area;
    for (int x = 0; x <= mx; x++)
      for (int y = 0; y <= my; y++)
        if (sheet[x][y]) {
          if (sheet[x+1][y])
            circumference -= 2;
          if (sheet[x][y+1])
            circumference -= 2;
        }
    cout << circumference << endl;
  }
}

