using namespace std;

short pool[FMAX + 1][FMAX];
short (*field)[FMAX] = pool + 1;
int main() {
  short n, r;
  short x1, y1, x2, y2;
  while (cin >> n >> r, n || r) {
    memset(field, 0, FMAX * FMAX * sizeof(short));
    short min_x = FMAX, min_y = FMAX, max_x = 0, max_y = 0;
    for (short i = 0; i < n; i++) {
      cin >> x1 >> y1 >> x2 >> y2;
      field[x1][y1] += 1;
      field[x1][y2] += -1;
      field[x2][y1] += -1;
      field[x2][y2] += 1;
      min_x = min(min_x, x1);
      min_y = min(min_y, y1);
      max_x = max(max_x, x2);
      max_y = max(max_y, y2);
    }
    // vertical
    for (short x = min_x; x <= max_x; x++) {
      short sum = 0;
      for (short y = min_y; y <= max_y; y++) {
        field[x][y] = (sum += field[x][y]);
      }
    }
    // horizontal
    int area = 0, perimeter = 0;
    for (short y = min_y; y <= max_y; y++) {
      short sum = 0;
      for (short x = min_x; x <= max_x; x++) {
        short current = field[x][y] = (sum += field[x][y]);
        if (current != 0) area++;
        if (r == 2) {
          perimeter += ((field[x - 1][y] == 0 ^ current == 0) + (field[x][y - 1] == 0 ^ current == 0));
        }
      }
    }

    cout << area << endl;
    if (r == 2) cout << perimeter << endl;
  }
  return 0;
}

