       x1 = a;
       y1 = b;
       x2 = c;
       y2 = d;
   }
   inbox(){};
   int g(int x,int y){
       return ((x1<=x&&x<x2&&y1<=y&&y<y2)?1:0);
   }
   int h(int x,int y){
       return (((x==x2||x==x1)&&(y1<=y&&y<y2))||((y==y2||y==y1)&&(x1<=x&&x<x2)))?1:0;
