#define INF (1<<29)
using namespace std;

typedef pair<int,int> P;

bool cmp_x( const pair<P,P> &p , const pair<P,P> &q ){
  return p.first < q.first;
}

bool cmp_y( const pair<P,P> &p , const pair<P,P> &q ){
  return p.second < q.second;
}

vector<pair<P,P> > in;

int main(void){

  int n,r,x1,x2,y1,y2;

  while(cin >> n >> r){
    if(n==0 && r==0)return 0;

    in.clear();
    int mnx=INF,mny=INF,mxx=0,mxy=0;
    for(int i=0;i<n;i++){
      scanf("%d %d %d %d",&x1,&y1,&x2,&y2);
      in.push_back(make_pair(make_pair(x1,x2), make_pair(y1,y2)));
      mnx=min(mnx,x1);
      mny=min(mny,y1);
      mxx=max(mxx,x2);
      mxy=max(mxy,y2);
    }

    sort(in.begin(),in.end(),cmp_y);

    int S=0,L=0;
    //vector<P>v;
    for(int i=mny;i<mxy;i++){
      //v.clear();
      priority_queue<P,vector<P> , greater<P> > v;
      for(int j=0;j<n;j++){
	if(in[j].second.second>=i+1 && i>=in[j].second.first){
	  //v.push_back(make_pair(in[j].first.first,in[j].first.second));
	  v.push(make_pair(in[j].first.first,in[j].first.second));
	}
	if(i<in[j].second.first)break;
      }

      if(v.size()!=0){
	//sort(v.begin(),v.end());
	int b=v.top().first,e=v.top().second;
	while(!v.empty()){
	  v.pop();
	  int B=v.top().first,E=v.top().second;
	  if(e>=B)e=max(e,E);
	  else S+=e-b, b=B, e=E, L+=2;
	}
	S+=e-b, L+=2;
      }
    }
    cout << S << endl;


    if(r==2){

      sort(in.begin(),in.end(),cmp_x);

      for(int i=mnx;i<mxx;i++){
	priority_queue<P,vector<P> , greater<P> > v;
	//v.clear();
	for(int j=0;j<n;j++){
	  if(in[j].first.second>=i+1 && i>=in[j].first.first){
	    //v.push_back(make_pair(in[j].second.first,in[j].second.second));
	    v.push(make_pair(in[j].second.first,in[j].second.second));
	  }
	  if(i<in[j].first.first)break;
	}
	if(v.size()!=0){
	  //	  sort(v.begin(),v.end());
	  int b=v.top().first,e=v.top().second;

	  while(!v.empty()){
	    v.pop();
	    int B=v.top().first,E=v.top().second;

	    if(e>=B)e=max(e,E);
	    else b=B, e=E, L+=2;
	  }
	  L+=2;
	}
      }
      cout << L << endl;
    }
  }
  return 0;
}

