#include<iostream>

const int MAX_W = 10010, MAX_H = 10010, INF = 1 << 20;

int map[MAX_W][MAX_H];

int main(){
	int n, m;
	while(std::cin >> n >> m, n){
		for(int y=0;y<MAX_H;y++){
			for(int x=0;x<MAX_W;x++){
				map[x][y] = 0;
			}
		}

		int min_x = INF, max_x = -1, min_y = INF, max_y = -1;

		for(int i=0;i<n;i++){
			int x1, y1, x2, y2;
			std::cin >> x1 >> y1 >> x2 >> y2;
			map[x1+1][y1+1]++;
			map[x1+1][y2+1]--;
			map[x2+1][y1+1]--;
			map[x2+1][y2+1]++;
			min_x = std::min(min_x, std::min(x1+1, x2+1));
			max_x = std::max(max_x, std::max(x1+1, x2+1));
			min_y = std::min(min_y, std::min(y1+1, y2+1));
			max_y = std::max(max_y, std::max(y1+1, y2+1));
		}

		int S = map[min_x][min_y]?1:0, L = 0;

		for(int y=min_y;y<=max_y;y++){
			for(int x=min_x+1;x<=max_x;x++){
				map[x][y] += map[x-1][y];
				if(y == min_y && map[x][y]){
					S++;
				}
			}
		}

		for(int y=min_y+1;y<=max_y;y++){
			for(int x=min_x;x<=max_x;x++){
				map[x][y] += map[x][y-1];
				if(map[x][y]){
					S++;
				}
			}
		}

		const int dx[4] = {-1, 0, 0, 1}, dy[4] = {0, -1, 1, 0};
		for(int y=min_y;y<=max_y;y++){
			for(int x=min_x;x<=max_x;x++){
				if(map[x][y]){
					int t = 0;
					for(int i=0;i<4;i++){
						int nx = x + dx[i], ny = y + dy[i];
						if(map[nx][ny])t++;
					}
					L += 4 - t;
				}
			}
		}

		std::cout << S << std::endl;
		if(m == 2)std::cout << L << std::endl;
	}
}

