#include <iostream>
using namespace std;
#define MAX 10000

bool map[MAX][MAX];

bool avR(int x, int y, int v) {
	if (v%2==0) {
		if (v/2==0) {//right
			if (y==0) return false;
			return map[x][y-1];
		} else {//left
			if (y==MAX) return false;
			return map[x][y+1];
		}
	} else {
		if (v/2==0) {//up
			if (x==MAX) return false;
			return map[x+1][y];
		} else {//down
			if (x==0) return false;
			return map[x-1][y];
		}
	}
}

bool avF(int x, int y, int v) {
	if (v%2==0) {
		if (v/2==0) {
			if (x==MAX) return false;
			return map[x+1][y];
		} else {
			if (x==0) return false;
			return map[x-1][y];
		}
	} else {
		if (v/2==0) {
			if (y==MAX) return false;
			return map[x][y+1];
		} else {
			if (y==0) return false;
			return map[x][y-1];
		}
	}
}

int main() {
	int n, r;
	int x1, x2, y1, y2;
	while (cin >> n >> r, (n||r)) {
		for (int x=0; x<MAX; x++)
		for (int y=0; y<MAX; y++)
			map[x][y] = false;
		int max[2] = { 0, 0 };
		int min[2] = { MAX, MAX };
		while (n--) {
			cin >> x1 >> y1 >> x2 >> y2;
			if (x1 < min[0]) min[0] = x1;
			if (y1 < min[1]) min[1] = y1;
			if (x2 > max[0]) max[0] = x2;
			if (y2 > max[1]) max[1] = y2;
			for (int x=x1; x<x2; x++) {
				for (int y=y1; y<y2; y++) {
					map[x][y] = true;
				}
			}
		}
		int S=0;
		for (int x=min[0]; x<max[0]; x++) {
			for (int y=min[1]; y<max[1]; y++) {
				if (map[x][y]) S++;
			}
		}
		cout << S << endl;
		if (r == 2) {//needed line length
			int l=0;
			int init[2] = { min[0], min[1] };
			for (;!map[init[0]][init[1]]; init[0]++);
			int x=init[0], y=init[1];
			int v=0; //0:right 1:up 2:left 3:down
			do {
				if (avR(x, y, v)) { //allow Right
					v = (4+v-1) % 4;//turn Right
				} else {
					l++;
					if (!avF(x, y, v)) { //deny Forward
						v = (v+1) % 4;//turn Left
						l++;
					}
				}
				//move
				if (v%2==0) {
					if (v/2==0) x++;//0:right
					else x--;//2:left
				} else {
					if (v/2==0) y++;//1:up
					else y--;//3:down
				}
			} while (!(x==init[0] && y==init[1]));
			l++;
			cout << l << endl;
		}
	}
	return 0;
}

