#define ALL(c) c.begin(),c.end()
#define RALL(c) c.rbegin(),c.rend()
#define SORT(x) sort((x).begin(),(x).end())
#define REP(i,x,y) for(int i=(x);i<(y);++i)
#define MP(a,b) make_pair((a),(b))
#define F_ first
#define S_ second

typedef long long int lli;
typedef pair<int,int> P;

const int INF=1<<30;
//const long long int INF_=1<<58;
const int dx[]={1,0,-1,0},dy[]={0,1,0,-1};

inline void Imos(vector<vector<int> > &plane){
	REP(y,0,plane[0].size()){
		REP(x,1,plane.size()){
			plane[x][y]+=plane[x-1][y];
		}
	}
	REP(x,0,plane.size()){
		for(int y=plane[0].size()-2; y>=0; --y){
			plane[x][y]+=plane[x][y+1];
		}
	}
}

inline int CalcSize(vector<vector<int> > &plane){
	int result=0;
	REP(x,0,plane.size()){
		REP(y,0,plane[0].size()){
			if(plane[x][y]>0) ++result;
		}
	}
	return result;
}

inline int BFS(vector<vector<int> > &plane,vector<vector<bool> > &has_done,int x,int y,int W,int H){
	int result=0;
	queue<P> que;
	que.push(MP(x,y));
	has_done[x][y]=true;
	while(!que.empty()){
		P p=que.front();
		que.pop();
		REP(i,0,4){
			int nx=p.F_+dx[i],ny=p.S_+dy[i];	
			if(0<=nx&&nx<W&&0<=ny&&ny<H&&!has_done[nx][ny]){
				if(plane[nx][ny]>0){
					que.push(MP(nx,ny));
					has_done[nx][ny]=true;
				}else ++result;
			}
		}
		//cout << result << ' ' << p.F_ << ' ' << p.S_ << endl;
	}
	return result;
}

inline int CalcLength(vector<vector<int> > &plane){
	vector<vector<bool> > has_done(plane.size(),vector<bool>(plane[0].size()));
	int result=0;
	REP(x,0,plane.size()){
		REP(y,0,plane[0].size()){
			if(!has_done[x][y]&&plane[x][y]>0){
				result+=BFS(plane,has_done,x,y,plane.size(),plane[0].size());
			}
		}
	}
	return result;
}

