#include <iostream>
#include <vector>
using namespace std;

const static int MAX = 10000;
bool Sheets[MAX+2][MAX+2];

int count(int x,int y) {
  int dx[4] = {-1,0,0,1};
  int dy[4] = {0,1,-1,0};
  int cnt = 0;
  for(int d=0;d<4;d++) {
    if(!Sheets[x+dx[d]][y+dy[d]])
      cnt++;
  }
  return cnt;
}

int main() {
  ios::sync_with_stdio(false);
  cin.tie(0);

  int n,r;
  while(cin >> n >> r) {
    if(n+r == 0) break;
    for(int i=1;i<=MAX;i++) {
      for(int j=1;j<=MAX;j++) {
        Sheets[i][j] = false;
      }
    }

    int x[2],y[2];
    for(int i=0;i<n;i++) {
      cin >> x[0] >> y[0] >> x[1] >> y[1];
      for(int w=x[0]+1;w<=x[1];w++) {
        for(int h=y[0]+1;h<=y[1];h++) {
          Sheets[h][w] = true;
        }
      }
    }

    // calc
    int length = 0;
    int surface = 0;
    for(int i=1;i<=MAX;i++) {
      for(int j=1;j<=MAX;j++) {
        if(Sheets[i][j]) {
          surface++;
          length += count(i,j);
        }
      }
    }
    cout << surface << endl;
    if(r==2) cout << length << endl;
  }
}

