                while(S[pos].first.second == i){
                    board[1][S[pos].first.first] += S[pos].second;
                    pos++;
                }
                //printf("%d, %d\n", i, pos);

                FOR(j, minX+1, maxX+1){
                    board[1][j] += board[1][j-1];
                }
            }

            FOR(j, minX, maxX+1){
                board[1][j] += board[0][j];
            }

            FOR(j, minX, maxX+1){
                //printf("%d, ", board[1][j]);

                if(board[1][j] > 0){
                    sq += 1;

                    if(j == 0 || board[1][j-1] == 0){
                        l += 1;
                    }
                    if(j == 10000 || board[1][j+1] == 0){
                        l += 1;
                    }
                    l += (board[0][j] == 0) * 2;
                }
            }
            //puts("");
            //printf("%d\n", l);

            std::swap(board[0], board[1]);
        }

        FOR(i, minX, maxX+1){
            if(board[0][i] > 0){
                l += 1;
            }
        }


        std::cout << sq << std::endl;
        if(R == 2){
            std::cout << l << std::endl;
        }
    }
}

