int calc_area(){
	int ans=0,imos[10001];
	rep(y,10000){
		memset(imos,0,sizeof imos);
		rep(i,n) if(y1[i]<=y && y<y2[i]) {
			imos[x1[i]]++;
			imos[x2[i]]--;
		}
		rep(x,10000){
			imos[x+1]+=imos[x];
			if(imos[x]>0) ans++;
		}
	}
	return ans;
}

int calc_peri(){
	int ans=0,imos[10001];
	rep(t,2){
		rep(y,10000){
			memset(imos,0,sizeof imos);
			rep(i,n) if(y1[i]<=y && y<y2[i]) {
				imos[x1[i]]++;
				imos[x2[i]]--;
			}
			rep(x,10000) imos[x+1]+=imos[x];
			rep(x,10001) if(imos[x]>0) {
				if(x==  0   || imos[x-1]==0) ans++;
				if(x==10000 || imos[x+1]==0) ans++;
			}
		}

		rep(i,n){
			swap(x1[i],y1[i]);
			swap(x2[i],y2[i]);
